css:
@-moz-document domain("192.168.68.2"), domain("decindustrie.3cx.no") {
.bg-black {
    background-color: #0a0c0e;
}
#chat-list #chatScrollableList, #chat-menu #chatScrollableList {
    background: rgb(20, 25, 30);
}
#chat-list #chatScrollableList .list-group li, #chat-menu #chatScrollableList .list-group li {
    background: rgb(40, 45, 50);
}
#chat-list #chatScrollableList .list-group li, #chat-menu #chatScrollableList .list-group li {
    border: 0;
    border-top: 1px solid rgba(100, 120, 140, 0.3);
    padding: 5px;
}
a {
    color: rgb(255, 255, 255, 0.75);
}
.bg-light.lter, .bg-light .lter {
    background-color: #14191e;
}
.b-b {
    border-bottom: 1px solid #5f7379;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgb(95, 115, 121);
}
.empty-chat-window {
    background: rgb(20, 25, 30);
}
.message-wrapper {
    background: rgb(10, 12, 14);
}
#chat-window .chat-massages-main {
    background-color: rgb(10, 12, 14);
}
.message-left .message-text {
    background-color: #282d32;
    padding-left: 15px;
}
.message-left .message-text.new-sender:before {
    border-color: transparent #282d32;
}
.message-left:hover .message-text {
    background-color: #33373c;
}
.message-left:hover .message-text.new-sender:before {
    border-color: transparent #33373c;
}
.bg-light {
    color: #a5a5a5;
}
.text-muted {
    color: #ffffff45;
}
.message-right .message-text {
    background-color: #124573;
}
.message-right .message-text.new-sender:after {
    border-color: transparent #124573;
}
.message-right:hover .message-text {
    background-color: #1d5486;
}
.message-right:hover .message-text.new-sender:after {
    border-color: transparent #1d5486;
}
.message-center .message-text {
    background-color: rgb(0, 0, 0);
}
#chat-window #chat-form-controls .input-restriction {
    background-color: #282d32;
}
#chat-window #chat-form-controls .message-input[contenteditable=true] {
    background-color: rgba(0, 0, 0, 0);
}
.b-r {
    border-right: 1px solid rgb(100, 120, 140);
}
.mhover:hover {
    background-color: rgba(255,255,255,0.1)!important;
    /* border-bottom: 1px dotted rgba(255,255,255,0.3); */
}
.emoji-mart {
    background: #171717;
}
.emoji-mart-category-label {
    height: 20px;
    background: rgba(0, 0, 0, 0.8);
    border-radius: 15px;
    margin-top: 5px;
}
.emoji-mart-category-label span {
    background: none;
    color: #a5a5a5;
    text-align: center;
    margin-top: 5px;
    padding-top: 0px;
}
.emoji-mart {
    border: none;
    color: #a5a5a5;
}
input {
    background-color: black;
}
.layout-type2-nav {
    background: rgb(20, 25, 30);
}
.list-group.no-bg .list-group-item {
    background-color: rgb(40, 45, 50);
}
.list-group.no-border .list-group-item {
    border-width: 1px 0;
    border-color: rgba(100, 120, 140, 0.3);
    color: #a5a5a5;
}
.list-group-item.focus {
    background-color: #383e40!important;
    color: white !important;
}
body {
    background-color: rgb(20, 25, 30);
}
.list-group-item {
    background-color: rgb(10, 12, 14);
    border-color: rgba(0, 0, 0, 0);
}
.list-group-item a.hover, .list-group-item a:focus, .list-group-item a:hover {
    background-color: transparent;
}
.panel-default>.panel-heading {
    color: #a6a9ab!important;
}
.panel-default>.panel-footer, .panel-default>.panel-heading {
    border-color: transparent;
}
.panel-default .panel-heading {
    background-color: rgb(30, 35, 40);
    border-bottom: 1px rgba(255, 255, 255, 0.5);
}
.panel-body {
    padding: 15px;
    background: rgb(40, 45, 50);
}
.panel-default {
    border-color: #64788b;
    margin-left: 5px;
}
.form-group {
    color: #a5a5a5;
}
.btn-default {
    color: #58666e;
    background-color: #080808;
    border-color: black;
}
.btn-default:hover {
    color: #ababab;
    background-color: #2d2d2d;
    border-color: #6d6d6d;
}
.form-control {
    border-color: #000000;
    border-radius: 2px;
    background-color: #080808;
    border: 1px solid #000;
}
.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: #080808;
    opacity: .5;
}
.bg-light {
    background-color: #191d21;
}
.flex-container {
    background-color: #14191e;
}
.panel {
    background-color: #0a0c0e;
}
.message-text .message-preview .border-picrure {
    height: auto;
    min-height: 70px;
    max-height: 400px;
    /* width: auto; */
    min-width: 200px;
    /* max-width: 1024px; */
    -webkit-filter: blur(0px);
    filter: blur(0px);
}
.message-text .message-preview img {
    align-self: center;
    height: 100%;
    max-width: 100%;
    /* background: white; */
    object-fit: contain;
    max-height: 400px;
}
.message-text .message-preview a {
    right: 0%;
    bottom: 0%;
    top: auto;
    left: auto;
    transform: inherit;
}
.message-text .message-file a {
    height: 30px;
}
.message-text .message-file a .message-file-name, .message-text .message-preview a .message-file-name {
    padding: 0;
}

.contact-editor {
	background: rgb(20, 25, 30);
}

/*modifs 10/01/20 */

.message-text .message-preview a {
    margin-top: 15px;
    margin-right: 15px;
    display: none;
}

.message-wrapper:hover .message-text .message-preview a {
    display: block;
}

span.message-file-name.nowrap {
    display: none;
}

.message-text .message-preview a {
    border-radius: 15px;
    background: url(https://image.flaticon.com/icons/png/512/402/402537.png) no-repeat center;
    background-size: 75%;
    background-color: rgb(255,255,255);
    box-shadow: 2px 2px 5px rgba(0,0,0,0.5);
    width: 32px;
    height: 32px;
    border: 0px solid rgba(0,0,0,0.5);
    top: 0%;
    bottom: auto;
}

.fillIconWhite {
    fill: #fff0 !important;
}
}
