css:
@-moz-document regexp("https?://(?:www\.)?(?:cnrfc|wrh)\.noaa\.gov/.*") {
a:visited {
    color: #d46dd4;
}
.center_left,
.footer-legal,
.header,
.footer,
.header-shadow-content,
.header-shadow {
    background-color: black;
    background: black;
}


ul#topnav .section-link a:link,
ul#topnav .section-link,
ul#topnav .home a {
    color: gray;
}
.topnav,
.topMenuNavList,
.normal,
.center-content,
.preRSA {
    background-color: #232323;
    color: #E0E0E0;
    font-size: medium;
}
.head {
    color: gray;
}
a,
a:link {
    color: #B0B0FF;
}

.standardTable td:first-child {
    background-color: #2f2f7c;
    color: #E0E0E0 !important;
}

td.white-background,
th.white-background,
table.standardTable2,
.standardTable2 td {
    background-color: gray !important;
}

td {
    font-size: medium;
}

.blueText,
p.blueText {
    color: #8c8ccd !important;
}

element.style {
    background-color: gray;
}

td.blue-background {
    background-color: #2f2f7c !important;
    color: #E0E0E0 !important;
    font-size: medium !important;
}
.standardTable td {
    background-color: #444444;
}

.footer-legal * {
    background-color: #232323;
    color: #E0E0E0;
    background: black;
}
}
