.views-row {
    width:75%;
    height:auto;
    padding:2rem;
    position:relative;
    overflow:hidden;
    margin:1rem;
    border-radius:2px;
    font-size:1rem;
    line-height:1.3rem;
    color:#000
}
.views-row:before {
    filter:blur(10px);
    content:"";
    position:absolute;
    left:-10px;
    top:-10px;
    width:calc(100% + 20px);
    height:calc(100% + 20px);
    z-index:-1
}
.views-row:after {
    content:"";
    position:absolute;
    left:0;
    top:0;
    right:0;
    bottom:0;
    z-index:-1;
    opacity:.7;
    border:1px solid #fff;
    background-color:#fff;
    background-image:url(https://s3-us-west-2.amazonaws.com/s.cdpn.io/59615/bg--acrylic-light.png)
