css:
@-moz-document url-prefix("https://homestuck2.com/") {
.bg-white {
    background-color : #999;
}
.bg-hs-gray {
    background-color : #555;
}
.bg-light-gray--md {
    background-color : #333;
}
/*link */
 a:link, a:hover {
    color: #f2a400;
}
/* visited link */
 a:visited, a:active {
    color: #cc8b00;
}
.type-hs-header {
    color : #FFF;
    text-shadow: 2px 2px 4px #CCC;
}
.chat-container {
    background-color : #222;
    border-style : solid;
    border-width: 3px;
    border-color: white;
    border-radius: 10px;
}
.chat {
    text-shadow: -1px -1px 2px #000, 1px -1px 2px #000, -1px 1px 2px #000, 1px 1px 2px #000;
}
.o_epilogue {
    color : #DDD;
    line-height: 1.6;
}
body {
    line-height: 1.7;
    color : #DDD;
    background-color : #222;
}
}
