css:
@-moz-document domain("lk.yali.edu.cn"){
    body {
        background: url('https://s2.ax1x.com/2019/10/26/KBtCgx.jpg') no-repeat fixed center left / cover;
    }
    .container {
        opacity: 0.80;
        color: #000000;
    }
    .mdui-color-theme {
        opacity: 0.85;
    }
    .mdui-list {
        color: #FFFFFF;
        background-color: #000000;
    }
    
    .mdui-drawer {
        opacity: 0.75;
    }
    
    .mdui-table {
        opacity: 0.80;
    }

    body {
        font-family: Roboto,Noto,Helvetica,Arial,sans-serif;
        font-size: 14px;
        color: #FFFFFF
    }
    
    .col {
        opacity: 0.80;
    }

    .hidden-xs {
        color: #FFFFFF;
    }

    .text-center {
        color: #FFFFFF;
    }

    .mdui-typo code {
        background-color: #000000;
    }

    .mdui-typo h1 {
        color: #FFFFFF;
    }

    .mdui-typo h2 {
        color: #FFFFFF;
    }

    .mdui-typo h3 {
        color: #FFFFFF;
    }

    .mdui-typo h4 {
        color: #FFFFFF;
    }

    .mdui-typo h5 {
        color: #FFFFFF;
    }

    .mdui-typo h6 {
        color: #FFFFFF;
    }

    .content {
        color: #FFFFFF;
        opacity: 0.80;
        background-color: #000000
    }

    .mdui-text-color-white {
        color: #FFFFFF!important;
    }

    .mdui-theme-primary-light-blue .mdui-color-theme {
        background-color: #000000 !important;
    }
    .mdui-color-white {
        background-color: #000000 !important;
    }
    .table {
        color: #FFFFFF!important;
        background-color: #000000!important;
    }
    .btn-primary {
        color: #FFFFFF;
        background-color: #000000;
        border-top-color: #000000;
        border-right-color: #000000;
        border-bottom-color: #000000;
        border-left-color: #000000;
    }
}
