css:
@-moz-document url-prefix("https://docs.google.com/document/d/") {
#docs-chrome{background: #333 none repeat scroll 0% 0% !important; color: #DDD !important;}
  
  #gb#gb a.gb_b, /* Account email in top right corner */
  .docs-presence-plus-widget-status /* x total viewers text */
  {color: #DDD !important;}
  
  /* Document title box, TOP HEADER. CHANGE THIS + SHARE BUTTON AREA FOR FULL HEADER. */
  .docs-title-input{
    background-color: #333333 !important;

    font-style: normal !important;
  }
  .docs-title-input:focus{
    color: white !important;
  }
.docs-gm #docs-titlebar-share-client-button .jfk-button {
background: rgb(206,137,45);
}

goog-inline-block jfk-button jfk-button-standard docs-titlebar-button{
background-color: white}

.docs-material-appbar #docs-header .docs-titlebar-buttons{
background-color: #333333
}
  .docs-title-untitled{display: none !important;}
  
  /* Fix for menu bar text when hover */
  .goog-control-hover{color: black !important;}
  
  /* Main toolbar */
  #docs-toolbar-wrapper{
    border-top: 1px solid #151516 !important;
    border-bottom: 1px solid #040404 !important;
    background-image: linear-gradient(#333333, #313131) !important;
    box-shadow: inset 0 1px rgba(255,255,255,0.15) !important;
  }
  /* Toolbar's vertical dividers */
  .goog-toolbar-separator.goog-inline-block{border-left: 1px solid #222 !important;}
  
.navigation-widget-hat-title {
color: white}

  /* A very simple/lazy fix to get toolbar item colors to look good on a dark background, INVERTS THE COLORS */
  .goog-toolbar-button,
  .goog-toolbar-toggle-button,
  .goog-toolbar-menu-button,
  .goog-toolbar-combo-button,
  .docs-icon-folder-solid{
    -webkit-filter: invert(1) !important;
    filter: invert(1) !important;
}
  /* Share buttom, User Icon. ADDS THE FIX FOR WHITE BLOB BEHIND SHARE */

.docs-title-input,
.docs-titlebar-buttons {
    background-color: #333333 !important;
    border-bottom: 1px solid #333333 !important;
    border-radius: 2px !important;
    border-top-color: #333333 !important;
    box-shadow: 0 0px rgba(255, 255, 255, 0.15) !important;
    font-style: normal !important;
}
  /* Highlighted selection and FIXES TEXT COLOR INVERSER */

.goog-menuitem.goog-menuitem-highlight,
.goog-menu.goog-menu-vertical.goog-menu-noicon.goog-menu-noaccel.goog-menuitem-highlight,
.goog-menuitem.goog-option.goog-option-selected.goog-menuitem-highlight,
.goog-menuitem.goog-option.goog-option-selected.goog-menuitem-selected,
.goog-menuitem.goog-option.goog-option-selected.goog-menuitem-hoover, 
.goog-color-menu-button-indicator,
.docs-icon-img-container.docs-icon-img.docs-icon-text-color,
.docs-icon-img-container.docs-icon-img.docs-icon-text-bgcolor{
    -webkit-filter: invert(1);
    filter: invert(1);
}
  /* Fixes for suggestion mode when editing own doc, viewing mode when editing others' docs */
  #docs-toolbar-mode-switcher, #docs-access-level-indicator{background-color: transparent !important; color: black !important;}
  .docs-icon-mode-review-white, .docs-icon-mode-view-white{filter: invert(1) !important;}
  
  /* Background behind the document, BACKGROUND COLOR CHANGE HERE*/
  #docs-editor{background: #404143 none repeat scroll 0% 0% !important;}

/* Change Outline Background Color */

.docs-material.docs-navigation-tab-button.goog-inline-block {
    background: rgb(243, 234, 217);
    border-color: rgb(243, 234, 217);
}

/* Interior outline Background Color */

.navigation-widget-navigation-tab-button.navigation-widget {
background: #333333;
}

/*Outline highlight BG */
.docs-gm .navigation-widget.navigation-location-indicator:not(.outline-refresh) .navigation-item.goog-button-hover {
background: rgb(45, 45, 45);
}

.docs-gm .navigation-widget:not(.outline-refresh) .navigation-item.goog-button-hover, .docs-gm .navigation-widget:not(.outline-refresh) .navigation-item.goog-button-focus, .docs-gm .navigation-widget:not(.outline-refresh) .location-indicator-highlight.navigation-item, .docs-gm .navigation-widget:not(.outline-refresh) .location-indicator-highlight.navigation-item.goog-button-hover, .docs-gm .navigation-widget:not(.outline-refresh) .location-indicator-highlight.navigation-item.goog-button-focus, .docs-gm .navigation-widget.navigation-location-indicator:not(.outline-refresh) .location-indicator-highlight.navigation-item, .docs-gm .navigation-widget.navigation-location-indicator:not(.outline-refresh) .location-indicator-highlight.navigation-item.goog-button-hover, .docs-gm .navigation-widget.navigation-location-indicator:not(.outline-refresh) .location-indicator-highlight.navigation-item.goog-button-focus {
    background: rgb(45, 45, 45);
}

/* Outline Text Color */
.docs-gm .navigation-widget-hat-title {
    color: white;
}
.docs-gm .navigation-item .navigation-item-level-0 {
    color: rgb(180,180,180);
}

/* Explore Background Color */
.docs-gm-sidebars .docs-explore-sidebar, .docs-gm-sidebars .docs-explore-searchbar-labelinput {
background: #333333;
}
.docs-gm-sidebars .docs-explore-emptylist-title {
    color: white;
}

.docs-gm-sidebars .docs-explore-sidebar-title {
background: #333333;
    color: grey;
}

.docs-gm-sidebars .docs-explore-sidebar-title {
 border: #333333   
}
    
/* Ruler background color */
.docs-gm .docs-horizontal-ruler, 
.docs-horizontal-ruler .docs-ruler-face, 
.docs-vertical-ruler .docs-ruler-face,
.docs-gm #kix-vertical-ruler {
background: #333333;
}

.docs-gm .docs-horizontal-ruler, 
.docs-gm #kix-vertical-ruler {
border-color: #333333;
}

/* BEGIN Sepia Background CHANGE SEPIA COLOR HERE*/

  .kix-page,
  .kix-page-compact,
  .kix-page-content-wrapper,
  .kix-paginateddocumentplugin,
  .kix-paginateddocumentplugin-compact-mode
  .docs-ui-unprintable {
    background-color: #F3EAD9 !important;
  }

  .kix-paginateddocumentplugin-background {
    display: none;
  }

/* END Sepia Background */
  
.menu-button {
    color: #959595 !important;
}

/* BEGIN Navigation (outline) widget text correction */

div.navigation-widget-hat {

/* Change this to change the outline text color. #CE892D is orange. */
}
.docs-gm-sidebars .navigation-widget-hat-title {
    color: #959595;
	}
.docs-gm-sidebars .navigation-item .navigation-item-level-0 {
    color: #959595;
	}
    
.docs-gm-sidebars .navigation-item .navigation-item-level-1 {
    color: #959595;
    }

/* END Navigation correction */

/* START Open Button Nav */

.companion-collapser-button .app-switcher-button-icon-container, .companion-collapser-button .app-switcher-button-icon-background {
    background-color: #f3ead9;
}

.docs-explore-widget {
    background: #f3ead9;
}

.companion-app-switcher-container {
    background-color: #333333;
    border-left: 1px solid #141416;
}

/* Scroll Bar Start */

.docs-gm-sidebars ::-webkit-scrollbar-thumb {
    background-color: #f3ead9;
}
/* Scroll Bar End */

.companion-collapser-button.app-switcher-button-checked .app-switcher-button-icon-container, .companion-theme-light .companion-collapser-button.app-switcher-button-checked .app-switcher-button-icon-container {
    background-color: #f3ead9;
}

  /* Document drop-shadow removal */
/* .kix-page-paginated{box-shadow: none !important;} */

/* Search function finder */
.docs-gm .docs-slidingdialog {
    background: rgb(50,50,50);
}

.docs-gm .docs-findinput-container, .docs-gm .docs-gotoinput-container {
    background: rgb(243, 234, 217);
}

.docs-gm .docs-findinput-container-focus,
.docs-gm .docs-gotoinput-container:focus {
    border-color: rgb(206,137,45) 
}

.kix-appview-editor {
    background-color: #333333 !important;
}
/* Kyle Li Says Hi! :D */
/* LiaisonUS.com */
}
