css:
@-moz-document url-prefix("https://twitchy.com") {
:root {
    --dark-bg-color: #101010;
    --text-bg-color: #303030;
    --text-darker-bg-color: #202020;
    --text-bg-gray-color: #404040;
    --text-color: #c0c0c0;    
    --text-dimmer-color: #a0a0a0;
    --text-less-color: #b0b0b0;
    --post-header-color: #5cb7d6;
    --post-timestamp-color: #0dce63;
    --post-rating-color: #88929c;
    --post-3dot-menu-color: #c0c0c0;
    --profile-text-color: #8a9ad4;
    --blue-tab-color: #053d63;
    --blueish-text: #386b7d;
    --blueish-text-medium: #397489;
    --blueish-text-lighter: #4e9cb8;
    --green-mint-text: rgb(17, 197, 97);
    --text-dblue-bg-color: #1d3a6e;
    --text-orange-color: #b20;
    --dark-transparant-background-color: rgba(16,16,16,.8);
    --selected-nav-color: #18c2fd;
    --black-box-border: rgb(68, 68, 68);
}

body {
    font-size: 14px;
    line-height: 1.42857143;
    color: var(--text-color);
    background-color: var(--text-bg-color);
}

h1.article {
    color: var(--text-color);
}

.article-text {
    color: var(--text-color);
}

.home-trending-widget-article ul li a {
    color: var(--text-color);
}

.article-text .small-card-bottom h4 a {
    color: var(--text-color);
}

.btn-default {
    background-color: var(--dark-bg-color);
}

.navbar-default {
    background-color: var(--dark-bg-color);
}

.navbar-default .navbar-nav>li>a {
    color: var(--text-color);
}

.navbar-default {
    border-color: black;
}

.large-card-bottom {
    background: var(--dark-bg-color);
}

.large-card a, .large-card h2, .large-horizontal-card a, .small-card a {
    color: var(--text-color);    
}

.large-card {
    border: 1px solid #444444;
    border-top-color: var(--black-box-border);
    border-right-color: var(--black-box-border);
    border-bottom-color: var(--black-box-border);
    border-left-color: var(--black-box-border);
    border-bottom: 3px solid var(--black-box-border);
    -webkit-box-shadow: 0 4px 8px 0 rgba(231,231,231,.5), 0 4px 20px 0 rgba(231,231,231,.5);
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.5), 0 4px 20px 0 rgba(0, 0, 0, 0.5);
    width: 100%;
}

.btn-group-justified .fb, .btn-group-justified .tw {
    border: 1px solid var(--black-box-border);
    -webkit-box-shadow: 0 4px 8px 0 rgba(231,231,231,.5), 0 4px 20px 0 rgba(231,231,231,.5);
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.5), 0 4px 20px 0 rgba(0, 0, 0, 0.5);
}

.home-trending-widget ol>li>a, .sidebar-trending-widget ol>li>a {
    color: var(--text-color);    
}

.home-trending-widget {
    border: var(--black-box-border);
    -webkit-box-shadow: 0 4px 8px 0 rgba(231,231,231,.5), 0 4px 20px 0 rgba(231,231,231,.5);
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.5), 0 4px 20px 0 rgba(0, 0, 0, 0.5);
}

.large-horizontal-card {
    border: var(--black-box-border);;
    -webkit-box-shadow: 0 4px 8px 0 rgba(231,231,231,.5), 0 4px 20px 0 rgba(231,231,231,.5);
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.5), 0 4px 20px 0 rgba(0, 0, 0, 0.5);
}

.newsletter-sign-up {
    filter: invert(100%);
    border: var(--black-box-border);
    -webkit-box-shadow: 0 4px 8px 0 rgba(255, 255, 255, .5), 0 4px 20px 0 rgba(255, 255, 255, .5);
    box-shadow: 0 4px 8px 0 rgba(255, 255, 255, .5), 0 4px 20px 0 rgba(255, 255, 255, .5);
}

blockquote {
    border-left: 5px solid var(--text-bg-gray-color);
}

#nav-share {
    background: var(--dark-bg-color);
}

#nav-share .article-nav-headline {
    color: var(--text-color);   
}

.thm-carousel {
    background: var(--dark-bg-color);
}

.thm-carousel .item {
    background: var(--dark-bg-color);
}

.thm-carousel__caption>a {
    color: var(--text-color);   
}

.home-trending-widget-article {
    border: 1px solid var(--dark-bg-color);
    -webkit-box-shadow: 0 4px 8px 0 rgba(231,231,231,.5), 0 4px 20px 0 rgba(231,231,231,.5);
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.5), 0 4px 20px 0 rgba(0, 0, 0, 0.5);
}

.small-card {
    border: 1px solid var(--dark-bg-color);
    -webkit-box-shadow: 0 4px 8px 0 rgba(231,231,231,.5), 0 4px 20px 0 rgba(231,231,231,.5);
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.5), 0 4px 20px 0 rgba(0, 0, 0, 0.5);
}
}
