css:
@-moz-document domain("youtube.com") {
/*
* Berny23's YouTube Enhancements v2.3
*/
ytd-rich-item-renderer {
	margin-right: 4px;
	display: inline-block;
	width: 210px;
	margin-bottom: 24px;
}

#avatar-link {
	display: none;
}

#video-title {
	font-size: 1.4rem !important;
}

#channel-name.ytd-video-meta-block {
	font-size: 1.3rem !important;
}

#metadata-line {
	font-size: 1.3rem !important;
}

ytd-mini-guide-renderer {
	display: none;
}

#contents {
	width: auto !important;
	max-width: 1332px !important;
}

ytd-two-column-search-results-renderer ytd-thumbnail:not(.ytd-movie-renderer), ytd-playlist-thumbnail {
	max-width: 246px !important;
	max-height: 138px !important;
}

.thumbnail-container.ytd-movie-renderer {
	min-width: 246px !important;
	max-width: 0px !important;
}

ytd-two-column-search-results-renderer {
	width: auto !important;
	/* max-width: 862px !important; */
}

ytd-app[mini-guide-visible_] ytd-page-manager.ytd-app {
	margin-left: 0px !important;
}
}
