css:
@-moz-document domain("cherp.chat") {
/* 1. Layout */
body {
    margin: 0;
    overflow-y: scroll;
    background-image: url('https://wallpaperaccess.com/full/1262772.jpg');
    background-attachment: fixed;
    background-position: top center;
    line-height: 1.5em;
    font-family: sans-serif;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    text-align: center;
    color: #aa1b55fc;
    word-wrap: break-word;
}

h2 {
    border: 10px;
    line-height: normal;
    padding-top: 10px;
    padding-bottom: 10px;
    padding-left: 10px;
    padding-right: 10px;
    border-color: #000;
    border-style: none;
    background-color: #aa1b55fc;
    color: #ffedf4;
    box-shadow: 0 1px 4px #600b1a;
}

/* 1.1. Header */
header {
    background-color: #e377a0;
}
header h1 {
    margin: 0;
    padding: 10px;
}
header a,
header img {
    display: block;
    margin: 0 auto;
}
header a {
    max-width: 565px;
}
header img {
    max-width: 100%;
}

/* 1.2. Navigation */
#nav {
    background-color: #aa1b55fc;
    color: #fff;
    text-align: center;
    position: -webkit-sticky;
    position: sticky;
    top: 0;
    right: 0;
    left: 0;
    z-index: 3;
}
#nav ul {
    margin: 0;
    padding: 0;
    list-style-type: none;
}
#nav li {
    display: inline-block;
}
#nav li:not(:last-child):after {
    content: " ★ ";
}
#nav a {
    display: inline-block;
    line-height: 30px;
    color: #fff;
}
#nav button {
    display: inline-block;
    margin: 0;
    padding: 0;
    border: 0;
    background: none;
    height: 30px;
    font: inherit;
    color: #fff;
    text-decoration: underline;
    cursor: pointer;
}
#nav button:hover {
    text-decoration: none;
}

@media (max-width: 640px) {
    #nav a,
    #nav button {
        position: relative;
        width: 40px;
        background-position: center;
        background-repeat: no-repeat;
        background-size: 30px;
        text-indent: -9000px;
    }
    #nav a {
        line-height: 40px;
    }
    #nav button {
        height: 40px;
    }

    a#nav_home {
        background-size: 32px;
        background-image: url('http://www.http://imgur.com/a/sk9o1');
    }
    a#nav_prompt_list {
        background-position: 8px 6px;
        background-image: url('http://www.http://imgur.com/a/sk9o1');
    }
    a#nav_directory {
        background-size: 24px;
        background-image: url('http://www.http://imgur.com/a/sk9o1');
    }
    a#nav_chat_list {
        background-position: center 3px;
        background-image: url('http://www.http://imgur.com/a/sk9o1');
    }
    a#nav_account {
        background-image: url('http://www.http://imgur.com/a/sk9o1');
    }
    button#nav_log_out {
        background-image: url('http://www.http://imgur.com/a/sk9o1');
    }

    #nav_chat_list:after {
        display: block;
        position: absolute;
        top: 10px;
        right: 7px;
        left: 7px;
        background-color: #fff;
        border-radius: 10px;
        text-indent: 0;
        line-height: normal;
        text-align: center;
        font-size: 10pt;
        font-weight: bold;
        color: #a7818e;
        content: attr(data-unread);
    }
}

/* 1.3. Main */
h2 {
    padding: 0;
    list-style-type: none;
    text-align: center;
}

main {
    display: block;
    margin: 30px;
    border: 10px;
    line-height: normal;
    padding-top: 10px;
    padding-bottom: 10px;
    border-color: #000;
    border-style: none;
    background-color: #f5dbe0c2;
    box-shadow: 0 1px 4px #711b5f;
}

h2 + main {
    margin-top: 0;
}

.side_column nav > :first-child {
    margin-top: 0;
}
.side_column h3 {
    font-size: 14pt;
}
.side_column ul {
    margin: 20px;
    padding: 0;
    list-style-type: none;
    text-align: center
}
.side_column p:first-of-type {
    margin-top: 0;
}

#content {
    max-width: 650px;
}
#content > :first-child {
    margin-top: 0;
}
#content > :last-child {
    margin-bottom: 0;
}

@media (max-width: 640px) {
    h2 {
        margin-top: 30px;
        margin-left: 15px;
        margin-right: 15px;
    }
    main {
        margin: 15px;
    }
}

@media (max-width: 1024px) {
    .side_column {
        max-width: 650px;
        margin: 20px auto;
        text-align: center;
    }
    .side_column li {
        display: inline;
    }
    .side_column li:not(:last-child):after {
        content: " ·";
    }
    #content {
        margin: 0 auto;
    }
}

@media (min-width: 1025px) {
    main.flex {
        display: flex;
        justify-content: center;
    }
    .side_column {
        flex: 0 1 180px;
        font-size: 10pt;
    }
    .side_column + .side_column {
        order: 1;
    }
    .side_column nav {
        position: sticky;
        top: 45px;
        max-width: 180px;
    }
    .side_column:first-child nav {
        margin-left: auto;
    }
    .side_column ul {
        text-align: left;
    }
    .side_column li {
        padding: 1px 5px;
    }
    .side_column li:not(:last-child) {
        border-bottom: 1px solid #FFD9E8;
    }
    .side_column li a {
        display: block;
        margin: -1px -5px;
        padding: 1px 5px;
    }
    main.flex #content {
        flex: 0 1 650px;
        padding: 0 30px;
    }
}

/* 2. General content */
h3 {
    margin-bottom: 0;
}

p {
    max-width: 650px;
    margin: 15px auto;
    line-height: 1.5em;
    word-wrap: break-word;
}

hr {
    margin: 15px 0;
    border: 0;
    border-top: 1px solid #ddd;
}

@media (max-width: 640px) {
    p {
        margin: 10px auto;
    }
    hr {
        margin: 10px 0;
    }
}

ul {
    padding-left: 1em;
}

a {
    color: #d05c97;
}
a:hover {
    text-decoration: none;
}

.danger h1,
.danger h2,
.danger h3,
.danger h4,
.danger h5,
.danger h6,
.danger a,
.error {
    color: #ca48ad;
}

/* 2.1. Forms */
input {
    vertical-align: middle;
}
input[type="checkbox"],
input[type="radio"] {
    margin: 0;
}
input.full,
textarea {
    margin: 0;
    padding: 0;
    border: 0;
    width: 100%;
    background-color: #fff;
    font: inherit;
    color: inherit;
}
label input.full {
    font-size: 1rem;
    color: #b76b6b;
}
textarea {
    resize: vertical;
}

/* 2.2. Tile */
.tile2 {
    position: relative;
    -webkit-column-break-inside: avoid;
    z-index: 2;
    box-sizing: border-box;
    max-width: 650px;
    margin: 20px auto;
    padding: 20px 25px;
    background-color: #fff;
    box-shadow: 0 1px 2px #bbb;
}
@media (max-width: 640px) {
    .tile2 {
        margin: 15px auto;
        padding: 10px 15px;
    }
}

.tile2 > :first-child {
    margin-top: 0;
}
.tile2 > :last-child {
    margin-bottom: 0;
}
.tile2 p {
    white-space: pre-line;
}

.tile2.pager {
    z-index: 1;
    margin: 0;
    padding-top: 10px;
    padding-bottom: 10px;
    background-color: #fff0;
    box-shadow: 0 1px 2px #961c1c00;
    text-align: center;
}
img.tile2 {
    display: block;
    padding: 0;
}

.subtitle,
.help,
label,
#prompt_dropdowns {
    font-size: 10pt;
    color: #ca48ad;
}
/* TODO put the last two selectors somewhere else */
.subtitle {
    text-align: center;
}
h3 + .subtitle,
h3 + .help {
    margin-top: 5px;
}

/* 2.3. Expandable */
.expandable .toggle {
    float: right;
    margin-left: 5px;
    outline: none;
}
.expandable .expanded_content,
.expandable.expanded .collapsed_content {
    display: none;
}
.expandable.expanded .expanded_content {
    display: block;
}
.expandable:last-child > p {
    margin-bottom: 0;
}

/* 2.4. Actions */
.actions {
    overflow: auto;
}
.actions > .left {
    float: left;
}
.actions > .right {
    float: right;
}
.middle_actions {
    text-align: center;
}
.actions form {
    display: inline;
}

/* 3. Pages */
/* 3.1. Sign up and log in */
#account_forms {
    text-align: center;
}

@media (min-width: 641px) {
    #account_forms {
        display: flex;
        justify-content: center;
        margin: 30px 0;
    }
    #account_forms form {
        margin: 0 15px;
    }
}

/* 3.2. Home */
.answer_mode #connecting,
.prompt_mode #connecting,
.wait_mode #connecting,
.connection_error #connecting,
#answer_mode,
#prompt_mode,
#wait_mode,
#connection_error {
    display: none;
}
.answer_mode #answer_mode,
.prompt_mode #prompt_mode,
.wait_mode #wait_mode,
.connection_error #connection_error {
    display: block;
}

#categories,
#categories textarea {
    background-color: #f8f8f8;
}
#categories ul {
    padding: 0;
    text-align: center;
}
#answer_categories {
    margin-bottom: 10px;
}
#answer_starters,
#answer_levels {
    margin: 10px 0;
}
#categories li {
    display: inline-block;
}
#categories li:not(:last-child):after {
    content: " ·";
}

#filter_toggle {
    display: none;
}
#filter_toggle + p {
    margin-bottom: 0;
    text-align: center;
    font-size: 10pt;
}
label[for="filter_toggle"] {
    color: #ab0737;
    text-decoration: underline;
    cursor: pointer;
}
label[for="filter_toggle"]:hover {
    text-decoration: none;
}
#filter_toggle + p + form,
#filter_toggle:checked + p {
    display: none;
}
#filter_toggle:checked + p + form {
    display: block;
}

#prompt_list {
    clear: both;
    margin: 20px 0;
    padding: 0;
    list-style-type: none;
    -moz-column-width: 500px;
    -moz-column-gap: 20px;
    -webkit-column-width: 500px;
    -webkit-column-gap: 20px;
}
#prompt_list li {
    max-height: 190px;
    overflow: hidden;
    cursor: pointer;
}
#prompt_list li:first-child {
    margin-top: 0;
}
#prompt_list li:last-child {
    margin-bottom: 0;
}
#prompt_list li p {
    margin: 0;
}
.fade {
    display: block;
    position: absolute;
    top: 140px;
    left: 0;
    right: 0;
    height: 50px;
    background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0), rgba(255, 255, 255, 1));
}
#prompt_text {
    height: 150px;
}

#overlay,
#report_overlay {
    display: none;
}
.show_overlay,
.show_report_overlay {
    overflow: hidden;
}
.show_overlay #overlay,
.show_report_overlay #report_overlay {
    display: block;
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 4;
    overflow-y: scroll;
    padding: 0 30px;
    background-color: rgba(238, 238, 238, 0.75);
}

#overlay_images {
    max-width: 650px;
    margin: 0 auto;
}
#overlay_images img {
    max-width: 100%;
}

@media (max-width: 640px) {
    #prompt_list li {
        max-height: 170px;
    }
    .fade {
        top: 120px;
    }
    .show_overlay #overlay,
    .show_overlay #report_overlay {
        padding: 0 15px;
    }
}

#prompt_info :first-child {
    margin-top: 0;
}

.banner {
    margin: 50px auto;
    text-align: center;
}

/* 3.3. Directory */
.request.draft,
.request.removed {
    background-color: #fbeff7;
}
.request .status {
    float: right;
    color: #666;
}
.request.removed .status {
    color: #f70000;
}
.request p:first-child,
.request .status + p {
    margin: 0 0 10px;
}
.request h3 {
    margin: 0 0 2px 0;
    text-align: left;
    font-size: 10pt;
    font-weight: normal;
    color: #000;
}
.request_tags {
    margin: 0 0 10px;
    padding: 0;
}
.request_tags li {
    display: inline;
    word-wrap: break-word;
}
.request_tags li:not(:last-child)::after {
    content: ",";
}
.request_tags li.trigger a {
    color: #000000;
}

#new_request_form .types {
    white-space: unset;
}
#new_request_form .types span {
    display: block;
    font-size: 10pt;
    color: #333;
}
#new_request_form .types label {
    display: inline-block;
    margin-right: 10px;
    font-size: 12pt;
    color: #000;
}

.tag_list {
    padding: 0;
    list-style-type: none;
}
.tag_list li {
    overflow: hidden;
}
.tag_list li:not(:first-child) {
    padding-top: 15px;
    border-top: 1px solid #ffe8f1;
}
.tag_list li:not(:last-child) {
    padding-bottom: 15px;
}
@media (max-width: 640px) {
    .tag_list li:not(:first-child) {
        padding-top: 10px;
    }
    .tag_list li:not(:last-child) {
        padding-bottom: 10px;
    }
}
.tag_list .remove_form {
    float: right;
}

table.tile2 {
    table-layout: fixed;
    max-width: 100%;
    padding: 0;
    border-collapse: collapse;
}
table.tile2 thead {
    background-color: #f8f8f8;
    border-bottom: 1px solid #eee;
}
table.tile2 tbody {
    font-size: 10pt;
}
table.tile2 td,
table.tile2 th {
    padding: 2px 5px;
    word-wrap: break-word;
}
table.tile2 tr:not(:first-child) td {
    border-top: 1px solid #eee;
}
table.tile2 tr:not(:last-child) td {
    border-bottom: 1px solid #eee;
}
table.tile2 td:not(:first-child),
table.tile2 th:not(:first-child) {
    border-left: 1px solid #eee;
}
table.tile2 td:not(:last-child),
table.tile2 th:not(:last-child) {
    border-right: 1px solid #eee;
}
table.tile2 a:only-child {
    display: block;
    margin: -2px -5px;
    padding: 2px 5px;
}

table.tile2 .unapproved a {
    color: #000000;
}
table.tile2 .synonym a {
    color: #ffe8f1;
}

.tag_input {
    margin: 0 0 10px;
    padding: 4px 8px;
    background-color: #f6f6f6;
    box-shadow: 0 1px 2px #ddd inset;
}
.tag_input .request_tags {
    margin: 0 0 2px;
}
.tag_input .request_tags:empty {
    margin: 0;
}
.tag_input .request_tags a {
    word-wrap: break-word;
}
.tag_input .request_tags a:hover {
    text-decoration: line-through;
}
.tag_input input {
    background-color: #f6f6f6;
    background-image: none;
}

.autocomplete_list {
    position: absolute;
    max-width: calc(100% - 52px);
    margin: -10px 0 0 1px;
    padding: 0;
    list-style-type: none;
    background-color: #fff;
    box-shadow: 0 1px 2px #bbb;
    cursor: pointer;
}
.autocomplete_list:empty {
    display: none;
}
.autocomplete_list li {
    padding: 4px 8px 4px 7px;
    word-wrap: break-word;
}
.autocomplete_list li.current {
    background-color: #f8f8f8;
}

/* 3.4. Chat list */
#chat_list {
    margin: 0;
    padding: 0;
    list-style-type: none;
}
#chat_list:first-child li:first-child {
    margin-top: 0;
}
#chat_list:last-child li:last-child {
    margin-bottom: 0;
}

.unread {
    box-shadow: 0 1px 4px #310a26;
}

.notes {
    margin: 5px 0 0;
    max-width: none;
    font-size: 10pt;
}

/* 3.5. Chat window */
body.ongoing main {
    margin-bottom: 0;
}

#messages {
    margin: 0;
    padding: 0 25px;
    list-style-type: none;
}
#messages li {
    position: relative;
    padding: 20px 0;
}
#messages li:not(:first-child) {
    border-top: 1px solid #ddd;
}
#messages li > p,
.delete_preview > p {
    margin: 0;
}
#messages .editing p {
    text-decoration: line-through;
}

.message_ooc {
    opacity: 0.5;
}
.message_ooc:hover {
    opacity: 1;
}
.message_system {
    text-align: center;
}

.symbol {
    position: absolute;
    width: 50px;
    left: -50px;
    text-align: center;
    font-size: 1.5em;
}
#message_form .symbol {
    margin-top: 15px;
}

.timestamp {
    font-size: 10pt;
    color: #888;
}
.edited .timestamp::after {
    content: " · Edited";
}

#status_bar {
    max-width: 70ex;
    margin: -5px auto;
    color: #666;
    text-align: center;
    font-size: 10pt;
}
#status_bar {
    max-width: none;
    margin: 0;
}

#message_form_container {
    margin-top: 0;
    margin-bottom: 0;
}
#message_form {
    position: relative;
}
#message_form p:first-child {
    margin-top: 0;
}
#message_form textarea {
    height: 100px;
}

.tile.continue {
    text-align: center;
}

.delete_preview {
    position: relative;
}

@media (max-width: 640px) {
    #messages {
        padding: 0 15px;
    }
    #messages li {
        padding: 10px 0;
    }
    .symbol {
        width: 30px;
        left: -30px;
        font-size: 1em;
    }
    #message_form .symbol {
        margin-top: 10px;
    }
}

#notification {
    display: none;
}
@media (min-width: 1025px) {
    #notification {
        position: fixed;
        right: 50px;
        bottom: 30px;
        width: calc(50% - 405px);
        min-width: calc(17.82% - 30px);
    }
    #notification_inner {
        position: relative;
    }
    #notification_close {
        float: right;
    }
}

/* 3.6. Chat info */
#chat_notes_notes,
#chat_notes_labels {
    font-size: 10pt;
}

/* 3.7. Account */
#confirmation:empty {
    display: none;
}
#confirmation:empty + .tile2 {
    margin-top: 0;
}
/* i really want this to be global*/
}
