css:
@-moz-document domain("crunchyroll.com") {
/*! Video Player !*/

#featured_carousel{
min-width: 900px !important;
}

#welcome_left{
width: 900px !important;
}
.welcome-simulcasts li {
    margin-right: 15px;
    margin-bottom: 12px;
}
	
#showmedia_video_player, #showmedia_video_box_wide {
    height: 720px !important;
    width: 1280px !important;
}

#showmedia_video{
    height: 720px !important;
    width: 1280px !important;
}

#showmedia{
    margin-top: 40px;
}

#main_content{
    width: 960px !important;
}

#template_container{
    width: 1280px !important;
}
#source_home .more-options {
    width: 960px !important;
}
#source_home #main_content .landscape-element {
    width: 865px;
}
#source_home #main_content .series-info {
    width: 730px;
}
#main_content textarea {
    width: 947px;
}
.collection-carousel{
    width: 936px !important;
}
.collection-carousel-contents{
    width: 890px !important;
}

.comments form textarea {
    max-width: 1036px !important;
}

.welcome-crnews-info, .welcome-news-info {
    width: 720px;
}
.welcome-latest-videos {
    width: 920px;
}
.welcome-latest-videos li {
    padding: 20px 22px 0 0;
}
.portrait-grid li {
    margin-left: 11px;
}
#source_showview .season-dropdown {
    background: url(https://crunchyroll.com/i/beta/videos_dropdown_arrow.png) 935px -11px no-repeat;
}
#source_showview .season-dropdown.open {
    background: url(https://crunchyroll.com/i/beta/videos_dropdown_arrow.png) 935px 12px no-repeat;
}
#source_showview .season-dropdown:hover {
    background: url(https://i.imgur.com/Q031uvq.png) 935px -11px no-repeat;
}
#source_showview .season-dropdown.open:hover {
    background: url(https://i.imgur.com/Q031uvq.png) 935px 12px no-repeat;
}
#sidebar.right {
    display: block;
    float: right;
    position: absolute;
    margin-left: 980px;
}
.news-left {
    width: 935px;
}
#message_box {
    display: none !important;
}  
}
