css:
@-moz-document regexp(".*anilist.co.*") {
/*General Adjustments*/
::-webkit-scrollbar {
    width: 10px!important;
    height: 7px!important;
}

::-webkit-scrollbar-thumb {
    cursor: pointer!important;
    background: rgb(11, 14, 17)!important;
    transition: .3s!important;
}

.text ::-webkit-scrollbar,
.about ::-webkit-scrollbar,
.activity-text ::-webkit-scrollbar {
    width: 0px!important;
}

.container {
    padding-left: 0px;
    padding-right: 0px;
}

#app > div.wrap > div {
    background: rgba(var(--color-foreground-grey-dark));
}

.emoji-spinner {
    display: none!important;
}

.list .details {
    padding: 10px 100px 10px 10px!important;
}

body {
    font-family: Arial !important;

}

.markdown .markdown-spoiler.spoiler-visible i.hide-spoiler {
    color: rgba(var(--color-blue), .9)!important;
    border-radius: 3px 0 3px 0;
    top: -5px;
}

.markdown a,
.markdown a:visited {
    transition: .2s;
}

.markdown a:hover,
.markdown a:visited:hover {
    text-shadow: 0px 0px 2px;
    transition: .2s;
}

.reply {
    line-height: 18px!important;
}

.shadow {
    display: none!important;
}

.status .title:hover,
.name:hover {
    text-shadow: 0px 0px 2px;
}

.status .title,
.name {
    font-weight: 500!important;
}

.primary-links{
	background-color: rgba(25,25,25) !important;
}

.footer{
	background-color: rgba(16,16,16) !important;

}

.container{
	background-color: rgba(16,16,16) !important;


}

.select{
	background-color: rgba(25,25,25) !important;


}

.search-wrap{
	background-color: rgba(25,25,25) !important;


}

.search{
	background-color: rgba(21,21,21) !important;

}

.hover-data{
	background-color: rgba(25,25,25) !important;

}

.media-card.has-rank{
	background-color: rgba(21,21,21) !important;

}

.hover-data.right{
	background-color: rgba(25,25,25) !important;


}

/*See everyone that likes your stuff*/
.like-wrap .users {
    overflow-y: scroll!important;
}

.like-wrap ::-webkit-scrollbar {
    width: 0px!important;
    -ms-overflow-style: none;
}

.users {
    background: rgba(0, 0, 0, .2)!important;
    border-radius: 4px 4px 0px 4px!important;
    min-height: 65px;
    min-width: 200px!important;
    right: -12px!important;
    top: -65px!important;
}

a.user {
    float: right;
    border-radius: 4px;
}

/*Dropdown menus are site theme based*/
.el-select-dropdown,
.el-dropdown-menu,
.el-dropdown-menu__item--divided::before {
    background: rgba(var(--color-foreground-blue-dark))!important;
}

.el-select-dropdown__item.hover,
.el-select-dropdown__item:hover {
    background: rgba(var(--color-foreground-grey-light));
}

.el-dropdown-menu__item--divided {
    border-color: rgba(var(--color-background));
}

.el-select-group__wrap:not(:last-of-type)::after {
    background: rgba(var(--color-foreground));
}

.el-popper[x-placement^="bottom"] .popper__arrow,
.el-popper[x-placement^="bottom"] .popper__arrow::after {
    border-bottom-color: transparent;
}

.el-popper[x-placement^="top"] .popper__arrow,
.el-popper[x-placement^="top"] .popper__arrow::after {
    border-top-color: transparent;
}

.el-select-dropdown.is-multiple .el-select-dropdown__item.selected,
.el-select-dropdown.is-multiple .el-select-dropdown__item.selected.hover {
    background: rgba(var(--color-foreground-grey-light));
}

/*Banners don't need shadows*/
.header-wrap .banner {
    margin-top: 0px;
}
.shadow {
    display: none;
}

/*Texteditor*/
.markdown-editor,
.el-textarea__inner {
    border-radius: 0;
}
.markdown-editor {
    margin-bottom: 2px;
}
.markdown-editor > div > svg {
    min-width: 1em!important;
}
.markdown-editor > [title="Image"],
.markdown-editor > [title="Youtube Video"],
.markdown-editor > [title="WebM Video"] {
    color: rgba(var(--color-red), .6);
}
.markdown-editor > [title="Youtube Video"]:hover {
    color: rgb(255, 0, 0);
}
.activity-entry .details .status:first-letter {
    text-transform: lowercase;
}

/*Donator Badge alignment*/
.details > .donator-badge {
    left: 105px!important;
    padding: 3px 5px 3px 5px!important;
    top: 100%!important;
    background: rgba(var(--color-foreground-blue));
    -ms-transform: translate(0px, -34px);
    /* IE 9 */
    -webkit-transform: translate(0px, -34px);
    /* Safari */
    transform: translate(0px, -34px);
    font-size: 1rem!important;
}
.activity-text > div > div > div > .donator-badge {
    position: relative!important;
    display: inline-block!important;
    left: 0px!important;
    top: -10px!important;
    -ms-transform: translate(0px, 0px);
    /* IE 9 */
    -webkit-transform: translate(0px, 0px);
    /* Safari */
    transform: translate(0px, 0px);
}
.site-theme-dark .details > .donator-badge {
    color: #fff;
}

/*Keeps long custom list names in the anime/manga editor in container(only works with hoh's script -> https://greasyfork.org/en/scripts/370473-aniscripts)*/
.list-editor-wrap .list-editor .body .checkbox .el-checkbox__label {
    word-break: normal;
}

/*Buttons*/
div.button.save {
    color: rgba(var(--color-text));
    background: rgba(var(--color-foreground));
    transition: .2s;
    width: 72px;
    margin: -5px 0 0 0;
    border-style: solid;
    border-radius: 3px;
    border-width: 1px!important;
    border-color: rgba(var(--color-text), .3);
}

div.save.button:hover {
    background: rgba(var(--color-foreground-blue));
    transition: .2s;
    margin-top: -5px;
    box-shadow: none;
}

.home .button.cancel,
.user .button.cancel,
.activity-entry .button.cancel,
.forum-thread .button.cancel {
    color: rgba(var(--color-text))!important;
    transition: .2s;
    margin: -5px 10px 0 0;
    width: 72px;
    padding-left: 16px;
    border-style: solid;
    border-radius: 3px;
    border-width: 1px!important;
    border-color: rgba(var(--color-text), .3);
}

.home .button.cancel:hover,
.user .button.cancel:hover,
.activity-entry .button.cancel:hover,
.forum-thread .button.cancel:hover {
    background: rgba(var(--color-foreground-blue));
    transition: .2s;
    margin: -5px 10px 0 0;
    box-shadow: none;
}

div.like-wrap:hover {
    color: rgb(232, 93, 117)!important;
    transition: .2s;
}

.replies > .count {
    color: rgb(61, 180, 242)!important;
}

.replies > *:nth-child(2) {
    color: rgb(61, 180, 242)!important;
}

/*Anime/manga editor*/
.list-editor-wrap .list-editor {
    margin-top: 5vh!important;
}

#app > div.el-dialog__wrapper.list-editor-wrap > div > div.el-dialog__body > div.body {
    padding-top: 40px;
}

#app > div.el-dialog__wrapper.list-editor-wrap > div > div.el-dialog__body > div.header {
    height: 140px!important;
}

.list-editor-wrap .list-editor .body .input-wrap {
    row-gap: 0px;
}

.list-editor-wrap .list-editor .body .checkbox {
    margin-bottom: 4px;
}

.list-editor-wrap .list-editor .body .custom-lists {
    margin-bottom: 4px;
}

body > div.el-picker-panel.el-date-picker.el-popper,
.el-date-picker__header-label {
    background: rgba(var(--color-background));
    border: none;
    color: rgba(var(--color-text));
}

body > div:nth-child(n) th {
    color: rgba(var(--color-text));
}

[type=reset],
[type=submit],
button,
html [type=button] {
    color: rgba(var(--color-text));
}

body > div:nth-child(n) > div.popper__arrow {
    display: none;
}

.el-date-table td.next-month,
.el-date-table td.prev-month {
    color: #606266;
}
}

@-moz-document regexp(".*anilist.co/.+") {
/*Global Customize Setting*/
/*Gravity Color Theme*/
.site-theme-dark {
    --color-text: 216, 216, 216;
    --color-text-light: 232, 232, 232;
    --color-text-lighter: 255, 255, 255;
    --color-nav: 27, 27, 27!important;
    --color-blue-dim: 120, 130, 150;
    --color-background: 21, 21, 21;
    --color-foreground: 38, 38, 38;
    --color-foreground-blue: 31, 31, 31;
    --color-foreground-grey: 25, 25, 25;
    --color-foreground-grey-dark: 16, 16, 16;
}

#app > .nav,
div#nav.nav.hide {
    height: 44px;
    background: rgba(var(--color-nav))!important;
}

/*Nav bar on user profiles*/
#app > div.page-content > div > div.header-wrap {
    position: sticky!important;
    top: -330px!important;
    z-index: 100;
}

/*Link Icons for the Nav Bar*/
.nav .wrap .links .link:before {
    display: inline-block;
    content: '';
    height: 16px;
    margin-top: -2px;
    width: 24px;
    background-size: 20px;
    margin-left: -14px;
    margin-bottom: -3px;
    background-repeat: no-repeat!important;
    background-position: center;
    filter: grayscale(100%) brightness(1.29);
    transition: .2s;
}

.nav .link[href*="/user/"]:hover::before,
.nav .link[href^="/forum/"]:hover::before,
.nav .link[href^="/search/"]:hover::before,
.nav .link[href^="/home"]:hover::before,
.nav .link[href^="/login"]:hover::before,
.nav .link[href^="/social"]:hover::before,
.nav .link[href^="/signup"]:hover::before {
    filter: grayscale(0%);
    transition: .2s;
}

#nav > div.wrap > div.links > a.link.router-link-exact-active.router-link-active[href^="/home"]::before,
#nav > div.wrap > div.links > a.link.router-link-exact-active.router-link-active[href*="/user/"]::before,
#nav > div.wrap > div.links > a.link.router-link-exact-active.router-link-active[href^="/forum/"]::before,
#nav > div.wrap > div.links > a.link.router-link-exact-active.router-link-active[href^="/search/"]::before,
#nav > div.wrap > div.links > a.link.router-link-exact-active.router-link-active[href^="/login"]::before,
#nav > div.wrap > div.links > a.link.router-link-exact-active.router-link-active[href^="/social"]::before,
#nav > div.wrap > div.links > a.link.router-link-exact-active.router-link-active[href^="/signup"]::before {
    filter: grayscale(0%)!important;
    transition: .2s;
}

.nav .link[href="/home"]::before {
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" version="1.1" width="96" height="96" viewBox="0 0 24 24"><path d="m12 3l-10 9h3v8h5v-6h4v6h5v-8h3z" fill="rgb(61,180,242)"/></svg>');
    background-size: 18.9px!important;
    transition: .2s;
}

.nav .link[href*="/user/"]::before {
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" version="1.1" width="96" height="96" viewBox="0 0 24 24"><path d="M5 3h14a2 2 0 0 1 2 2v14a2 2 0 0 1 -2 2h-14a2 2 0 0 1 -2 -2v-14a2 2 0 0 1 2 -2zM12 6a3 3 0 0 0 0 6a3 3 0 0 0 0 -6zM6 18h12v-1a6 3 0 0 0 -12 0z" fill="rgb(61,180,242)"/></svg>');
    transition: .2s;
    background-size: 18.4px!important;
}

.nav .link[href*="/animelist"]::before,
.nav .link[href*="/mangalist"]::before {
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" version="1.1" width="96" height="96" viewBox="0 0 24 24"><path d="M4 5h4v4h-4zM4 10h4v4h-4zM4 15h4v4h-4zM9 5h12v4h-12zM9 10h12v4h-12zM9 15h12v4h-12z" fill="rgb(61,180,242)"/></svg>');
    transition: .2s;
}

.nav .link[href^="/search/"]::before {
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" version="1.1" width="96" height="96" viewBox="0 0 24 24"><path d="M4 5h5v13h-5zM10 5h11v6h-11zM10 12h5v6h-5zM16 12h5v6h-5z" fill="rgb(61,180,242)"/></svg>');
    transition: .2s;
}

.nav .link[href*="/forum"]::before {
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" version="1.1" width="96" height="96" viewBox="0 0 24 24"><path d="M19 6h2a1 1 0 0 1 1 1v15l-4 -4h-11a1 1 0 0 1 -1 -1v-2h13zM3 2a1 1 0 0 0 -1 1v14l4 -4h10a1 1 0 0 0 1 -1v-9a1 1 0 0 0 -1 -1z" fill="rgb(61,180,242)"/></svg>');
    transition: .2s;
    background-size: 16px!important;
}

.nav .link[href="/login"]::before {
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" version="1.1" width="96" height="96" viewBox="0 0 24 24"><path d="m12 3l-10 9h3v8h5v-6h4v6h5v-8h3z" fill="rgb(61,180,242)"/></svg>');
    background-size: 18.9px!important;
    transition: .2s;
}

.nav .link[href="/social"]::before {
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" version="1.1" width="96" height="96" viewBox="0 0 24 24"><path d="M5 3h14a2 2 0 0 1 2 2v14a2 2 0 0 1 -2 2h-14a2 2 0 0 1 -2 -2v-14a2 2 0 0 1 2 -2zM12 6a3 3 0 0 0 0 6a3 3 0 0 0 0 -6zM6 18h12v-1a6 3 0 0 0 -12 0z" fill="rgb(61,180,242)"/></svg>');
    transition: .2s;
}

.nav .link[href="/signup"]::before {
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" version="1.1" width="96" height="96" viewBox="0 0 24 24"><g fill="rgb(61,180,242)"><circle cx="15" cy="8" r="3"/><path d="M9 18h12v-1a6 3 0 0 0 -12 0z"/><text x="2" y="14" font-weight="bold" font-size="70%">+</text></g></svg>');
    background-size: 24px!important;
    transition: .2s;
}



/*UI correction(ACTIVATE ONLY IF YOU'RE USING HOH'S USERSRIPT -> https://greasyfork.org/en/scripts/370473-aniscripts)*/
#app {
    overflow: hidden!important;
}

.page-content > .container,
.notifications-feed,
.page-content > .studio {
    margin-top: 30px !important;
}

.home {
    margin-top: -30px!important;
}
}

@-moz-document regexp(".*anilist.co/home"), regexp(".*anilist.co/social") {
/*Home Adjustments*/
.list-preview {
    grid-gap:5px!important;
    padding: 7px!important;
    background: rgba(38, 38, 38)!important;
}

.small .cover {
    border-radius: 3px!important;
}

.home {
    grid-column-gap: 30px!important;
    grid-template-columns: auto 465px!important;
}

.search {
    margin-top: 0px!important;
}

.icon {
    padding: 0px !important;
}

.wrap {
    overflow: visible!important;
}

#app > div.page-content > div > div > div.activity-feed-wrap > div.activity-feed > div:nth-child(n) > div > div.list > a {
    border-radius: 3px 0 0 3px;
}

.media-preview {
    grid-gap: 10px!important;
    padding: 0px!important;
    background: rgba(0, 0, 0, 0)!important;
}

.media-preview-card {
    display: inline-grid!important;
}

.home .activity-feed-wrap h2.section-header {
    transition: .2s;
}

.section-header {
    padding-left: 0px!important;
}

#app > div.page-content > div > div > div.activity-feed-wrap > div.activity-edit > div.input.el-textarea {
    margin-bottom: 10px!important;
}

.input.el-textarea,
.activity-entry.full-width.activity-anime_list,
.activity-entry.full-width.activity-manga_list,
.activity-entry.full-width.activity-text {
    margin-bottom: 0px!important;
}

.list-preview .media-preview-card .content,
.media-preview .media-preview-card .content {
    background: rgba(var(--color-foreground), .9)!important;
}

/*Feed type Flollowing/Global*/
.feed-type-toggle {
    margin-left: 10px;
    background: transparent!important;
}

#app > div.page-content > div > div > div.activity-feed-wrap > h2 > div > div.feed-type-toggle > div:nth-child(n) {
    background: rgba(var(--color-foreground));
    margin-left: -3px;
}

#app > div.page-content > div > div > div.activity-feed-wrap > h2 > div > div.feed-type-toggle > div.active {
    background-color: rgba(var(--color-foreground-blue));
}

/*Like and comment buttons*/
div.like-wrap:hover {
    color: rgb(232, 93, 117);
    transition: .2s;
}

.replies > .count {
    color: rgb(61, 180, 242);
}

.replies > *:nth-child(2) {
    color: rgb(61, 180, 242);
}

/*All links are blue*/
#app > div.page-content > div > div > div:nth-child(2) > div.list-previews > div:nth-child(1) > div.section-header > h2 {
    color: rgba(var(--color-blue));
}

#app > div.page-content > div > div > div:nth-child(n) > div.list-previews > div:nth-child(n) > div.section-header > h2:hover {
    text-shadow: 0px 0px 2px!important;
}

h2.section-header.link {
    color: rgba(var(--color-blue));
}

h2.section-header.link:hover {
    text-shadow: 0px 0px 2px!important;
}

.home .activity-feed-wrap h2.section-header {
    padding-top: 10px;
}

#app > div.page-content > div > div > div.activity-feed-wrap > h2 {
    padding-top: 0px;
}

#app > div.page-content > div > div > div:nth-child(2) > div.list-previews > div:nth-child(2) > div.section-header > h2:hover,
#app > div.page-content > div > div > div:nth-child(2) > div.list-previews > div:nth-child(3) > div.section-header > h2:hover {
    text-shadow: none!important;
}

/*Activities are seperated by color*/
.activity-text .text {
    border-left: solid 5px rgb(61, 180, 242);
    border-radius: 3px;
}

.activity-manga_list > div > div > div > div > .title {
    color: rgb(232, 93, 117)!important;
}

/*Activity distribution*/
.activity-entry .status {
    display: inline-block!important;
    line-height: 1.6rem;
    padding-right: 115px;
}

.avatar {
    display: block!important;
}

.reply {
    margin-bottom: 2px!important;
    border-radius: 0px!important;
}

.header a:nth-child(1) {
    display: inline-block!important;
}

.list {
    min-height: 80px!important;
    grid-template-columns: 60px auto!important;
}

.list .name {
    padding-bottom: 0!important;
}

.activity-replies {
    margin-top: 2px!important;
}

.reply-wrap .activity-replies .activity-edit .markdown-editor {
    border-radius: 0;
    margin: 0px 0px 2px 0px;
}

.activity-text .markdown {
    overflow-wrap: break-word;
    word-break: normal;
    margin: 10px 0 10px 0!important;
}

.reply-wrap .activity-replies .activity-edit .actions {
    margin: 10px 0 -5px 0;
}

.input.el-textarea {
    margin-bottom: 10px;
}

.activity-entry {
    margin-bottom: 10px!important;
}

.text {
    padding: 15px 10px 20px 10px!important;
}
}

@-moz-document regexp(".*anilist.co/home") {
/*Home Customize Settings*/
/*Extended Feed Filter*/
.feed-select .el-dropdown-menu.el-popper.el-dropdown-menu--small {
    display: flex!important;
    text-align: center!important;
    box-shadow: none;
}

.feed-filter {
    display: none;
}

.activity-feed-wrap .section-header .el-dropdown-menu {
    background: none!important;
    position: static;
    display: inline !important;
    padding: 0!important;
    border-radius: 3px;
}

.activity-feed-wrap .section-header .el-dropdown-menu__item {
    color: rgb(255, 255, 255);
    font-size: 1.2rem;
}

.activity-feed-wrap .el-dropdown-menu__item:nth-child(n) {
    background-color: rgba(var(--color-foreground));
    color: rgba(var(--color-text-lighter));
    transition: .2s;
    width: 106px;
    height: 25px;
    line-height: 2.5rem;
}

.activity-feed-wrap .el-dropdown-menu__item:nth-child(2) {
    margin: 0 1px 0 1px;
}

.activity-feed-wrap .el-dropdown-menu__item.active:nth-child(n) {
    background: rgba(var(--color-foreground-blue));
    color: rgba(var(--color-text));
    line-height: 2.5rem!important;
    font-weight: 500;
}

.activity-feed-wrap .el-dropdown-menu__item:nth-child(n):hover {
    color: rgba(var(--color-blue));
    transition: .2s;
}
}

@-moz-document regexp(".*anilist.co/user/.*") {
/*User Adjustments*/
.header-wrap .banner .name {
    background: rgba(0, 0, 0, .5);
    border-radius: 16px;
    padding: 6px 15px;
    margin: 15px 5px 15px 25px;
    height: 29px;
    text-shadow: none;
}

.user .overview {
    grid-template-columns: 465px auto!important;
}

.section-header {
    padding-left: 0px!important;
}

.user-page-unscoped.custom-color .progress .bar:after {
    background: rgba(var(--color-blue))!important;
}

.el-slider__button-wrapper {
    z-index: 0;
}

/*Mod badge*/
#app > div.page-content > div > div.header-wrap > div.banner > div.container > div > div.mod-badge {
    margin-bottom: 19px!important;
    cursor: default;
    height: 20px;
    border-radius: 10px;
}

/*Follow/Donator*/
.nav-btn {
    height: 35px;
}

/*Genre overview from Kuwabara*/
.genre .name {
    background: rgba(var(--color-foreground-grey))!important;
    border-radius: 2px;
    font-size: 1.3rem;
}

.genre .amount {
    font-size: 1.2rem;
    font-weight: 500;
}

.status:nth-of-type(1) .name {
    color: rgb(104, 214, 57);
}

.status:nth-of-type(2) .name {
    color: rgb(2, 169, 255);
}

.status:nth-of-type(3) .name {
    color: rgb(146, 86, 243);
}

.status:nth-of-type(4) .name {
