css:
@-moz-document domain("web.telegram.org") {
body, .tg_head_split, .im_page_wrap {
    background-color: black;
    color: white;
    border-color: black;
}

.im_page_wrap {
    border: none;
}

.im_dialogs_col_wrap {
    border-color: #1e272e;
}

.im_message_unread_split {
    background-color: #1e272e;
    color: #00d8d6;
}

.tg_head_peer_dropdown .dropdown-menu {
    background-color: #1e272e;
    border-color: #1e272e;
    color: white;
}

.tg_head_peer_dropdown .dropdown-menu > li > a {
    color: white;
}

a.im_dialog .im_short_message_text, .im_dialog_peer {
    color: #d2dae2;
}

.im_dialogs_scrollable_wrap .active a.im_dialog {
    background-color: #1e272e;
    color: #00d8d6;
}

.dropdown-menu {
    background-color: #1e272e;
    border-color: #1e272e;
    color: white;
}

.tg_head_logo_dropdown .dropdown-menu > li > a {
    color: #d2dae2;
}

.tg_head_logo_dropdown .dropdown-menu {
    border-color: #1e272e;
}

.im_dialogs_scrollable_wrap a.im_dialog:hover, .im_dialogs_scrollable_wrap a.im_dialog_selected {
    background-color: #1e272e;
    color: #00d8d6;
}

.im_message_selected .im_message_outer_wrap {
    background-color: #1e272e;
    color: white;
}

.im_history_select_active .im_message_outer_wrap:hover {
    background-color: #1e272e;
    color: white;
}

.im_record_bg, .im_record_bg * , .im_record_bg i {
    background-color: #1e272e;
    color: white;
    fill: white;
}

a.tg_checkbox,p.tg_checkbox {
    color: white;
}

h1, h2, h3, h4, h5 {
    color: white;
}

.im_dialogs_search_field {
    background-color: #1e272e;
    border-color: #1e272e;
    color: white;
    border-radius: 20px;
}

.md-input-disabled .md-input {
    color: #d2dae2;
    text-decoration: line-through;
}

.md-input-group {
    border-color: #1e272e;
}

.md-input {
    background-color: black;
    border-color: black;
    color: white;
}

.modal-content {
    background-color: black;
}

.composer_dropdown_wrap {
    background-color: black;
}
}
