@-moz-document url-prefix("https://www.tumblr.com/settings/") {
/* SETTINGS */


body {
    font-family: helvetica;
    font-size:12px!important;
}




/* BACKGROUND */

.identity {
    background-color: #202020;
}




/* TOP BAR */

.l-header-container--refresh {
    box-shadow: 0 0 0 1px hsla(0,0%,100%,.13);
    transform: translateZ(0) translateY(0);
    z-index: 90;
    background-color: #151515;
    position: fixed;
    width: 100%;
}


.l-header-container--refresh .l-header {
    box-sizing: border-box;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    position: relative;
    width: 100% !important;
    max-width: 1580px;
    left: 0;
    top: 0;
    min-width: inherit;
    padding-top:14px!important;
    height:82px;
}


.ui_search .search_form_field::before {
    transform: scaleY(.9);
    content: "\EA99";
    transition: color .15s;
    position: absolute;
    font-size: 29px;
    color: inherit;
    z-index: 2;
    left: 15px;
    top: 7px;
}


.ui_search .search_query {
    padding-right: 10px;
    padding-bottom: 2px;
    padding-left: 39px;
    line-height: inherit;
    font-family: helvetica !important;
    font-size: 12px;
    opacity:1!important;
    height: 34px;
    width: 100%;
    border: 0;
    border-radius: 3px;
    margin-top: 10px;
}


.tab_bar .tab_anchor {
    transition: opacity 0ms;
    margin-top: -2px;
    margin-right:0px!important;
    margin-left:0px!important;
    height: 35px;
    text-decoration: none;
    text-align: center;
    text-indent: 0;
    display: block;
    font-size: 0;
    color: #fff;
    float: right;
    opacity: .65;
    background: none;
    overflow: hidden;
}


.tab.iconic.tab_xkit {
    margin-top:0px;
    height:50px;
}


.compose-button {
    position: relative;
    cursor: pointer;
    height: 36px;
    width: 49px;
    margin-left: 16px!important;
    margin-right:-4px;
    margin-top: 9px;
