css:
@-moz-document url-prefix("https://www.youtube.com/watch?") {
#date.ytd-video-primary-info-renderer {
    font-size:48px;
    color:white;
    font-weight:normal;
    position: absolute;
    float: left;
    margin-top:-505px;
    margin-left:-100px;
    -webkit-text-stroke-width: 0.4px;
    -webkit-text-stroke-color: black;
    text-shadow: 2px 2px #333;
    font-family:'Dot Matrix';
    background-color: rgba(0, 0, 0, 0.6);
    padding:15px 10px 15px 70px;
    border-radius: 15px;
    opacity: 1;
    transition: opacity .25s ease-in-out;
   -moz-transition: opacity .25s ease-in-out;
   -webkit-transition: opacity .25s ease-in-out;
}
.title.ytd-video-primary-info-renderer {
    font-size:36px;
    position: absolute;
    float: left;
    margin-top:-165px;
    margin-left:-60px;
    background-color: rgba(0, 0, 0, 0.8);
    padding:15px 5px 25px 70px;
    border-radius: 15px;
    -webkit-text-stroke-width: 0.4px;
    -webkit-text-stroke-color: black;
    text-shadow: 2px 2px #333;
    font-family:'Dot Matrix';
    word-wrap: break-word;
    width:850px;
    line-height:28px;
    opacity: 1;
    transition: opacity .75s ease-in-out;
   -moz-transition: opacity .75s ease-in-out;
   -webkit-transition: opacity .75s ease-in-out;
}
#avatar img.yt-img-shadow {
    position: absolute;
    float: left;
    margin-top:-558px;
    margin-left:0px;
    border-radius: 15px;
    box-shadow: 1px 2px 4px rgba(0, 0, 0, .5);
    border:2px black;
    opacity: 1;
    transition: opacity .65s ease-in-out;
   -moz-transition: opacity .65s ease-in-out;
   -webkit-transition: opacity .65s ease-in-out;
}
#primary div#text-container.style-scope.ytd-channel-name {
    position: absolute;
    float: left;
    margin-top:-510px;
    margin-left:-100px;
    font-family:'Dot Matrix';
    background-color: rgba(0, 0, 0, 0.6);
    padding:5px 5px 5px 70px;
    border-radius: 15px;
    text-transform: uppercase;
    letter-spacing: 10px;
    font-size:24px;
    text-align:right;
    opacity: 1;
    transition: opacity 5s ease-in-out;
   -moz-transition: opacity 5s ease-in-out;
   -webkit-transition: opacity 5s ease-in-out;
}
#related , #primary:hover #avatar img.yt-img-shadow , #primary:hover #date.ytd-video-primary-info-renderer {
    opacity: 0;
    transition: opacity .55s ease-in-out;
   -moz-transition: opacity .55s ease-in-out;
   -webkit-transition: opacity .55s ease-in-out;
}

#primary:hover div#text-container.style-scope.ytd-channel-name {opacity: 0;
    transition: opacity 5s ease-in-out;
   -moz-transition: opacity 5s ease-in-out;
   -webkit-transition: opacity 5s ease-in-out;}


#primary:hover .title.ytd-video-primary-info-renderer {opacity: 0;
    transition: opacity .95s ease-in-out;
   -moz-transition: opacity .95s ease-in-out;
   -webkit-transition: opacity .95s ease-in-out;}

#primary:hover #date.ytd-video-primary-info-renderer {
    opacity: 0;
    transition: opacity .45s ease-in-out;
   -moz-transition: opacity .45s ease-in-out;
   -webkit-transition: opacity .45s ease-in-out;
}
}
