css:
@-moz-document domain("optionsflow.aidedtrade.com") {
/*Green #0f9d58*/
/*Red #d23f31*/

html
{
  background: #161616;
}

.help-map
{
  filter: invert(100%) hue-rotate(180deg);
  -webkit-filter: invert(100%) hue-rotate(180deg);
}

.flow-container
{
  border: none !important;
}

.flow-container > h1, .flow-container > p
{
  background: #161616;
  color: #FAFAFA;
}

.flow-stats
{
  background: #161616 !important;
}

.flow-stats > .stat
{
  background: #2C2C2C;
  color: #FAFAFA;
}

.flow-stats > .stat > .small
{
  background: #161616;
}

.flow-stats > .stat > .small::after
{
  background: #1C1C1C !important;
}

.flow-stats > .stat > .small > .label
{
  color: #FAFAFA;
}

.flow-top
{
  background: #161616 !important;
  border-bottom: solid lightgrey 1px;
}

.flow-top > .flow-top-controls
{
  background: #161616;
}

.flow-top > .flow-top-controls > .house > img
{
  filter: invert(100%);
  -webkit-filter: invert(100%);
}

.flow-top > .flow-headings
{
  background: #161616;
  color: #FAFAFA;
}

.flow-top > .flow-headings > span > img
{
  filter: invert(100%);
  -webkit-filter: invert(100%);
}

.flow-table > .flow-row:nth-child(even)
{
  background: #1C1C1C;
}

.flow-table > .flow-row:nth-child(odd)
{
  background: #2C2C2C;
}

.flow-table > .flow-row
{
  color: #FAFAFA;
}

.flow-table > .flow-row > .ticker.bull
{
  background: #0f9d58;
}

.flow-table > .flow-row > .ticker.bear
{
  background: #d23f31;
}

.flow-table > .flow-row > .premium.bull
{
  color: #0f9d58;
}

.flow-table > .flow-row > .premium.bear
{
  color: #d23f31;
}

.flow-table > .flow-row > .info > img
{
  filter: invert(100%);
  -webkit-filter: invert(100%);
}

}
