css:
@-moz-document domain("swiss-badminton.ch") {
:root {
    --bg-color: #151515;
    --highlight-color: #008c2c;
    --txt-color: #cccccc;
}


.body *,
.main,
.navbar,
.topmenu,
.topmenu .nav
{
    background: var(--bg-color) !important;
     color: var(--txt-color) !important;
}

.navbar .firstactive,
.navbar-default .navbar-nav .dropdown-menu .active > a
{
     background: var(--highlight-color) !important;
}

.navbar-nav ul li a:focus, .navbar-nav ul li a:hover
{
     background: #222222 !important;
     color: var(--txt-color) !important; 
}

.navbar .firstactive .dropdown-toggle
{
         color: var(--txt-color) !important; 
}

.topmenu
{
    border-bottom: solid var(--highlight-color)  5px  !important;
}

footer
{
 border-top: solid var(--highlight-color) 5px  !important;   
}


a 
{
    color: var(--highlight-color) !important;
}

a.profile
{
    filter: hue-rotate(290deg);
}

.usermenu *,
.dropdown-menu *
{
     background-color: var(--bg-color) !important;
     color: var(--txt-color) !important;
}

.dropdown-menu .active 
{
    background-color: var(--highlight-color) !important;
         color: blue !important;

}

#tbxSearchQuery
{
     background-color: #333333 !important;
     color: var(--txt-color) !important;
}

.c12,
#organizationmenu,
.leftcolumn, .groupheader .contextmenu,
.c12 h1, .c12 h2, .c12 h3, .c12 h4,
.ruler thead th,
.map__list
{
     background-color: var(--bg-color) !important;
     color: var(--txt-color) !important;
}

#ctl02_content_ctl00_cphPage_omOrganizationGroupMenuNew .active a
{
    border-left: solid 5px var(--highlight-color) !important;
}

tr th.lightgreen,
#content .importexport .green,
#content td.green
{
    background-color: var(--bg-color) !important;
    border-bottom-color: var(--txt-color) !important;
}

span.chart
{
    filter: invert(100%)
}

.defaultrowdescription,
.defaultrow
{
     background-color: var(--bg-color) !important;
     color: #dddddd !important;
     border-bottom: #444444 solid 1px !important;
}

tr.msinactive,
tr.msinactive *
{
    background-color: #222222 !important;
}

.tabs li
{
    background-color: var(--bg-color) !important;
}

body.layout-is-old #organizationmenu .tabs li a, 
body.layout-is-old #organizationmenu li label, 
body.layout-is-old #statistics li a, 
body.layout-is-old #tournamentsubmenu li a, 
body.layout-is-old .tabs li a 
{
	color: var(--txt-color) !important;
}

body.layout-is-old #organizationmenu .tabs li a.active, 
body.layout-is-old #organizationmenu li label.active, 
body.layout-is-old #statistics li a.active, 
body.layout-is-old #tournamentsubmenu li a.active, 
body.layout-is-old .tabs li a.active 
{
	color: var(--txt-color) !important;
    background-color: var(--highlight-color) !important;
}

.chosen-container,
.chosen-container .chosen-single,
.chosen-container .chosen-single span,
.chosen-container .chosen-single div,
.chosen-container .chosen-drop,
input,
select,
.partnername, .partnermemberid, #ctl02_content_ctl00_cphPage_cphPage_VF0_fs0_remarkscontrol
{
     background-color: #333333 !important;
     color: #dddddd !important;    
}

#btickerFixturesTable h2
{
    background-color: #333333 !important
}

.navbar-brand, .logo, #endnote {
	filter: invert(100%);
}

#newsbar .post
{
     background-color: var(--bg-color) !important;
     color: #dddddd !important;    
     border: solid 2px #444444;
}

.block h3
{
    border-bottom: solid 2px var(--highlight-color) !important;
}

.lcmenu li.active,
.lcmenu li.active a
{
    background-color: var(--highlight-color) !important;
    border-color: var(--highlight-color) !important;
    color: #dddddd !important;
}

.stepsinfo,
.tournamentinfo
{
     background-color: #333349 !important;
     color: var(--txt-color) !important;  
     border: #000044 4px solid !important
}

table.ruler caption 
{
        color: var(--txt-color) !important;     
}

table tr.promote * 
{
     background-color: #555555 !important;
     color: #dddddd;        
}

table tr.demote * 
{
     background-color: #333333 !important;
     color: #dddddd;        
}

table.ruler tbody tr:hover
{
         background-color: #272727 !important;
}

table th.orderby,
table th.orderby *
{
     background: #181818 !important;
     color: var(--txt-color) !important;   
}

button,
.button,
#btnAddContact,
.pagenumbers .page_selected
{
    background-color: var(--highlight-color) !important;
    border: #444444 !important; 
}

.pagenumbers
{
     border-top: 10px solid var(--bg-color);  
}
.pagenumbers a
{
    background-color: var(--bg-color) !important;
    color: var(--txt-color) !important;
    border: solid 2px var(--txt-color) !important;
}

.page_caption, .pagesize
{
    background-color: var(--bg-color) !important;
    color: var(--txt-color) !important;
}

#btnCancel
{
    background-color: #444444 !important;
    border: #444444 !important;
}

footer
{
     background-color: var(--bg-color) !important;
     color: var(--txt-color) !important;
}
}

@-moz-document url-prefix("http://example.com") {

}
