css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dailynews.yahoo.co.jp") {
/* delete ads */
 div#adAms,
 div#adSqb
   {
  display: none !important;
   }

/* delete header */
 div#uhd
   {
  display: none !important;
   }

/* delete Sidebar */
 div#sub
   {
  display: none !important;
   }

/* delete footer */
 div#adS,
 div#footer
   {
  display: none !important;
   }

/* custom design */
 div#wrapper,
 div#main,
 div#mainMargin
   {
  max-width : 800px    !important;
  min-width : 600px    !important;
  margin    : 0px auto !important;
  float     : none     !important;
   }

 div#masthead
   {
  margin : 10px 0px !important;
   }

}
