css:
/* i really want this to be global */

.user-tagline-rating {
    color: #fff !important;
}

.user-tagline-rating {
    color: hsla(0,0%,100%,1) !important;
}

.arena-tab-component.arena-tab-darkMode div, .arena-tab-component.arena-tab-darkMode label, .arena-tab-component.arena-tab-darkMode p, .arena-tab-component.arena-tab-darkMode select, .arena-tab-component.arena-tab-darkMode span, .arena-tab-component.arena-tab-darkMode textarea {
    color: 
    hsl(0, 0%, 100%);
}

.arena-standings-component.arena-standings-darkMode .arena-standings-row, .arena-standings-component.arena-standings-darkMode .arena-standings-row .arena-standings-score, .arena-standings-component.arena-standings-darkMode .arena-standings-row .arena-standings-streak {
    color: 
    hsl(0, 0%, 100%);
}

.arena-header-component.arena-header-darkMode .arena-header-info, .arena-header-component.arena-header-darkMode .arena-header-info h4 {
    color: 
    hsl(0, 0%, 100%);
}

.arena-header-component.arena-header-darkMode .arena-header-actions .arena-header-timer {
    background-color: 
rgba(0,0,0,.4);
color:
    hsl(0, 0%, 100%);
}

.tournament-tab-component.tournament-tab-darkMode div, .tournament-tab-component.tournament-tab-darkMode label, .tournament-tab-component.tournament-tab-darkMode p, .tournament-tab-component.tournament-tab-darkMode select, .tournament-tab-component.tournament-tab-darkMode span, .tournament-tab-component.tournament-tab-darkMode textarea {
    color: 
    hsl(0, 0%, 100%);
}

.tournament-standings-component.tournament-standings-darkMode .tournament-standings-row {
    color: hsl(0, 0%, 100%);
}

.tournament-standings-component .tournament-standings-row .tournament-standings-tiebreak {
    color: 
    #fff;
}

.tournament-tab-component.tournament-tab-darkMode .tournament-tab-info, .tournament-tab-component.tournament-tab-darkMode .tournament-tab-info h4 {
    color: 
    hsl(0, 0%, 100%);
}

.tabset-nav-component.tabset-nav-darkMode.tabset-nav-tabsetChild .tabset-nav-item.tabset-nav-iconized .tabset-nav-tabIcon {
    color: 
    hsl(0, 0%, 100%);
}

.team-match-tab-component.team-match-tab-darkMode div, .team-match-tab-component.team-match-tab-darkMode label, .team-match-tab-component.team-match-tab-darkMode p, .team-match-tab-component.team-match-tab-darkMode select, .team-match-tab-component.team-match-tab-darkMode span, .team-match-tab-component.team-match-tab-darkMode textarea {
    color: 
    hsl(0, 0%, 100%);
}

.clock-playerTurn {opacity:1}
.clock-component {opacity:1}
