css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zasshi.news.yahoo.co.jp") {

/* delete header */
 div#uhdMain,
 div#SQB
   {
  display: none !important;
   }

/* delete sidebar */
 td.magSub
   {
  display: none !important;
   }

/* delete footer */
 div#footer
   {
  display: none !important;
   }

/* custom design */
 div#wrapper,
 div#newsSearch,
 div#globalNav,
 table.magContents
   {
  max-width: 800px !important;
  min-width: 600px !important;
  margin   : 0px auto !important;
   }

 div.yjmth,
 table.magContents
   {
  margin : 10px 0px !important;
   }
}
