css:
@-moz-document domain("forum.politz.com.br") {
  .hScroller-action--end,
  .hScroller-action--start {
    background: #d65a31 !important;
  }
  .p-header-content:before {
    content: "Bem vindo ao Halloween Politz";
    animation: horror 5s infinite;
    color: transparent;
    text-shadow: 0 0 5px rgba(0, 0, 0, 0.5);
    font-family: "Denk One", sans-serif;
    text-transform: uppercase;
    letter-spacing: 2px;
    font-size: 2em;
    margin-left: 195px;
  }
  .hScroller-action {
    background: orange;
  }
  .button {
    color: #000 !important;
    background: #d65a31 !important;
  }
  .button.button--primary {
    border-color: #eee;
  }
  .button--icon--attach:hover,
  .button--icon--preview:hover,
  .button--icon--write:hover,
  .button.button--primary:hover {
    animation: pulse 0.2s infinite;
  }
  .button.button--link {
    color: #000 !important;
  }
  .button-text {
    color: #eee !important;
  }
  .button--icon--write {
    border-color: #000 !important;
  }
  .button--cta .button-text {
    font-weight: 600;
    letter-spacing: 0.2px;
    text-transform: uppercase;
  }
  .button-text:hover {
    color: #fff !important;
    animation: fadein 0.5s;
  }
  .logo-desktop {
    display: none;
  }
  .p-header-content:after {
    position: relative;
    left: -90%;
    background-image: url(https://i.imgur.com/ynDxplM.png);
    content: "";
    width: 120px;
    height: 120px;
    margin: 0;
    padding: 0;
  }
  @-webkit-keyframes rotation {
    from {
      -webkit-transform: rotate(0);
    }
    to {
      -webkit-transform: rotate(359deg);
    }
  }
  @keyframes horror {
    5% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.2);
      left: 50%;
      top: 35%;
    }
    0% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.1);
      left: 51%;
      top: 36%;
    }
    10% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.3);
      left: 49%;
      top: 34%;
    }
    15% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.4);
      left: 50%;
      top: 35%;
    }
    16% {
      text-shadow: 0 0 5px #212121;
      left: 50%;
    }
    17% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.4);
      left: 50%;
    }
    20% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.5);
      left: 49%;
    }
    25% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.6);
      left: 49%;
    }
    30% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.7);
      left: 50%;
    }
    35% {
      text-shadow: 0 0 5px rgba(255, 0, 0, 0.8);
      left: 50%;
    }
    36% {
      text-shadow: 0 0 5px #212121;
      left: 50%;
    }
    37% {
      text-shadow: 0 0 5px rgba(255, 0, 0, 0.8);
      left: 50%;
    }
    40% {
      text-shadow: 0 0 5px rgba(255, 0, 0, 0.9);
      left: 51%;
    }
    45% {
      text-shadow: 0 0 5px rgba(255, 0, 0, 0.8);
      left: 51%;
    }
    50% {
      text-shadow: 0 0 5px rgba(255, 0, 0, 0.7);
      left: 51%;
    }
    55% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.6);
      left: 50%;
    }
    56% {
      text-shadow: 0 0 5px #212121;
      left: 50%;
    }
    57% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.6);
      left: 52%;
    }
    60% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.5);
      left: 50%;
    }
    65% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.4);
      left: 50%;
    }
    70% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.3);
      left: 49%;
    }
    75% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.2);
      left: 49%;
    }
    76% {
      text-shadow: 0 0 5px #212121;
      left: 50%;
    }
    77% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.2);
      left: 49%;
    }
    80% {
      text-shadow: 0 0 5px rgba(255, 0, 0, 0.1);
      left: 49%;
    }
    85% {
      text-shadow: 0 0 5px rgba(255, 0, 0, 0.2);
      left: 51%;
    }
    86% {
      text-shadow: 0 0 5px #212121;
      left: 50%;
    }
    87% {
      text-shadow: 0 0 5px rgba(255, 0, 0, 0.2);
      left: 51%;
    }
    90% {
      text-shadow: 0 0 5px rgba(255, 0, 0, 0.3);
      left: 51%;
    }
    95% {
      text-shadow: 0 0 10px rgba(255, 0 0, 0.4);
      left: 51%;
    }
    100% {
      text-shadow: 0 0 10px rgba(255, 0, 0, 0.5);
      left: 50%;
    }
  }
  .p-nav {
    background-color: #d65a31 !important;
    box-shadow: 0 14px 28px rgba(0, 0, 0, 0.25), 0 10px 10px rgba(0, 0, 0, 0.22);
  }
  .p-nav-list,
  .p-navgroup-linkText {
    font-family: Creepster, cursive;
    font-size: 16px;
    letter-spacing: 2.5px;
    text-transform: lowercase;
  }
  .p-navEl:hover {
    color: #0c093c;
  }
  .xb-content-wrapper {
    background-image: url(https://i.imgur.com/IUaHs5r.png);
  }
  .message-inner .message-cell--user {
    position: relative;
    left: 1vh;
    margin-right: 60px;
    margin-bottom: 20px;
    background-image: url(https://i.imgur.com/vSjvpS1.png),
      linear-gradient(rgba(210, 90, 75, 1), rgba(255, 0, 0, 2)) !important;
    background-blend-mode: lighten, lighten;
    border-radius: 0.3vh !important;
    border: 2px solid #fff1e9;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1),
      0 2px 4px -1px rgba(0, 0, 0, 0.06);
  }
  .message-cell--user {
    margin-top: 6vh;
    margin-left: 2vh;
    height: 10%;
  }
  .message-userArrow {
    display: none;
  }
  .fa-medal {
    color: #fff1e9;
  }
  .message-name .username {
    display: block;
    width: 110%;
    text-align: center;
    font-family: Roboto, sans-serif;
    font-size: 15px;
    color: #eee;
    font-weight: 700;
    background: #eee;
    padding: 1px;
    position: relative;
    top: -136px;
    left: -5px;
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1),
      0 4px 6px -2px rgba(0, 0, 0, 0.05);
    border: 1px solid #fff;
    border-radius: 2px;
    background-image:
    linear-gradient(rgba(214, 90, 49, 1), rgba(214, 90, 49, 0.7)) !important;
    transition: background 2s;
  }
  .message-name .username:hover {
    background: #393e46 !important;
    color: #fff;
    text-decoration: none;
    animation: pulse 0.2s infinite;
  }
  .message-userDetails .userTitle {
    display: block;
    width: 122%;
    font-family: Roboto, sans-serif;
    font-size: 15px;
    font-weight: 600;
    text-transform: capitalize;
    text-shadow: 0 13.36px 8.896px #c4b59d, 0 -2px 1px #fff;
    color: #393e46;
    background: #eee;
    position: relative;
    top: -20px;
    left: -14px;
    padding: 1px 3px;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1),
      0 2px 4px -1px rgba(0, 0, 0, 0.2);
  }
  .ah-gp-icon .fa-discord,
  .ah-gp-icon .fa-facebook,
  .ah-gp-icon .fa-playstation,
  .ah-gp-icon .fa-steam,
  .ah-gp-icon .fa-twitter,
  .ah-gp-icon .fa-xbox,
  .ah-gp-icon .fa-youtube {
    color: #fff;
    font-size: 25px;
    transition: color 0.5s;
  }
  .ah-gp-icon .fa-discord:hover,
  .ah-gp-icon .fa-facebook:hover,
  .ah-gp-icon .fa-playstation:hover,
  .ah-gp-icon .fa-steam:hover,
  .ah-gp-icon .fa-xbox:hover,
  .ah-gp-icon .fa-youtube:hover {
    color: orange;
    -webkit-animation: rotation 1s infinite linear;
  }
  .userBanner.userBanner--orange {
    background: #fff;
    color: orange;
    margin: 1px 10px 20px 10px;
  }
  .message-avatar-wrapper .avatar {
    clip-path: polygon(
      0 15%,
      15% 15%,
      15% 0,
      85% 0,
      85% 15%,
      100% 15%,
      100% 85%,
      85% 85%,
      85% 100%,
      15% 100%,
      15% 85%,
      0 85%
    );
    border: 3px solid #fff;
    transition: width 2s;
  }
  .actionBar-action--delete:hover,
  .actionBar-action--edit:hover,
  .actionBar-action--reply:hover,
  .actionBar-action--report:hover {
    background: #d65a31;
    text-decoration: none;
    color: #fff;
    animation: pulse 0.5s infinite;
  }
  .actionBar-action--delete,
  .actionBar-action--edit,
  .actionBar-action--mq,
  .actionBar-action--reaction,
  .actionBar-action--reply,
  .actionBar-action--report {
    padding: 3px;
    color: #d65a31;
    transition: color 0.8s;
  }
  .message-attribution {
    border-bottom: 1px solid orange;
  }
  .featuredBadges:before {
    content: "Medalhas:";
    font-family: Roboto;
    display: block;
    margin-bottom: 7px;
    color: #ffff;
    border-bottom: 1px solid #fff;
  }
  .message-user .featuredBadges {
    text-align: center;
    display: block;
    width: 120%;
    padding: 2px;
    position: relative;
    left: -13px;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1),
      0 2px 4px -1px rgba(0, 0, 0, 0.06);
    margin-bottom: 15px;
    background-color: rgba(0, 0, 0, 0.1);
  }
  .fa-crown:hover,
  .fa-gift:hover,
  .fa-medal:hover {
    -webkit-animation: rotation 0.8s infinite linear;
  }
  .badgeIcon--fa {
    color: #fff;
  }
  .message-userExtras {
    display: block;
    width: 100%;
    position: relative;
    left: 1px;
    font-family: Roboto;
    font-size: 14px;
    font-weight: 700;
    color: #fff;
  }
  .message-userExtras .pairs {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1),
      0 2px 4px -1px rgba(0, 0, 0, 0.06);
    background-color: rgba(0, 12, 10, 0.1);
    margin-top: 2px;
  }
  .ah-gp-icons-container {
    text-align: center;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1),
      0 2px 4px -1px rgba(0, 0, 0, 0.06);
    margin-top: 14px;
  }
  .pairs > dt {
    font-size: 13px;
    color: #fff;
  }
  @keyframes pulse {
    0% {
      transform: scale(0.95);
      box-shadow: 0 0 0 0 rgba(0, 0, 0, 0.7);
    }
    70% {
      transform: scale(1);
      box-shadow: 0 0 0 10px transparent;
    }
    100% {
      transform: scale(0.95);
      box-shadow: 0 0 0 0 transparent;
    }
  }
}
