@-moz-document url(http://www.myspace.com/), url-prefix(http://myspace.com/), url-prefix(http://www.myspace.com/index.cfm?fuseaction=signout){




/** log out page **/

#tkn_medrec, #aspnetForm{display:none!important;}





/** log in page and logout page **/

#topnav, #leaderboard, #googleSearch, #googlead2, #squareAd, #col2, #findFriends, #marketing, #searchContainer_Header, #splashlinks, #Object1, #footer, #loginTabs {display:none!important;}

body, #wrap, #content, #header, #googlebar {
background: #000 !important;
margin: 0 auto !important;
padding: 0 !important;}

#logIn {background-image: url(http://i41.tinypic.com/28u37dk.png)!important;}

#content{background-color:black!important;}

body, #wrap {background-color:black!important;}

#LoginForm  { visibility:visible!important;}

#mslogo{
position: absolute!important;
background-color:black!important;
top:50px!important;
margin-left:-90px!important;
left:50%!important;
z-index:1!important;  
visibility:visible!important;}

 #col1 {
position: absolute!important;
background-color:black!important;
top:100px!important;
margin-left:-200px!important;
left:50%!important;
width:400px!important; 
height:200px!important;
z-index:1!important;  
visibility:visible!important;}


}
