css:
@-moz-document domain("pl.ign.com") {
body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #BF1313 #1c1c1c;
}
/*HEADER*/
header{
    background: #1c1c1c;
}
#Group path#IGN{
    fill: #fff;
}
#ignheader .editions p{
    color: #fff;
}
#logomenu a{
    color: #fff;
}
#headermenuwrapper{
    color: #fff;
}
#topmore,#ignheader .editions ul{
    background: #1c1c1c;
    border-bottom: 1px solid #BF1313;
}
#topmore div.col ul li a,#ignheader .editions ul span.option a{
    color: #fff;
}
#topmore div.col ul li a:hover,#ignheader .editions ul span.option a:hover{
    color: #BF1313;
}
#logomenu li a{
    color: #BF1313;
}
.dropdown-arrow .icon-down-arrow{
    stroke: #fff;
}
#searchwrapper li.search > svg{
    filter: invert();
}
/*HEADER*/
/*GHEADER*/
#gheader{
    background: #BF1313;
}
#gheader li a{
    color: #fff;
}
/*GHEADER*/
/*PAGE CONTENT*/
section.vspotlight{
    background: #1c1c1c;
}
/*PAGE CONTENT*/
/*HSPOTLIGHT*/
section.hspotlight{
    background: #1c1c1c;
}
section h2{
    color: #BF1313;
}
section.hspotlight .caption a{
    color: #fff;
}
/*HSPOTLIGHT*/
/*BROLL*/
section.broll{
    background: transparent;
}
section.broll h3 a,section.broll div.m > p{
    color: #fff;
}
/*BROLL*/
/*FOOTER*/
#footer.limitedScroll{
    background: #505050;
}
.legal span{
    color: #fff;
}
/*FOOTER*/
/*SERAILE*/
.herogrid{
    background: transparent;
}
.publish-date,.blocktitle{
    color: #fff !important;
}
.title{
    color: #BF1313;
}
.description{
    color: #ccc;
}
.item-title{
    color: #fff;
}
/*SERAILE*/
/*NEWS*/
.page-content > div:not(.ad-wrapper){
    background: transparent;
}
.object-info{
    background: #1c1c1c !important;
}
.article-headline h1{
    color: #BF1313;
}
#id_deck,.article-publish-date{
    color: #ccc;
}
#id_text > p{
    color: #fff;
}
.twitter-tweet{
    background: #15202B;
    color: #fff;
    border-radius: 12px;
}
.object-summary{
    background: transparent;
}
.object-details{
    background: transparent !important;
}
a.evidence{
    color: #BF1313;
}
.object-details{
    color: #ccc;
}
.ign-recommends-headline{
    color: #BF1313;
}
/*NEWS*/
/*REKOMENDACJE*/
.ign-recommends-item .item-title-link{
    color: #fff;
}
/*REKOMENDACJE*/
/*POLITYKA PRYWATNOSCI*/
#id_text ul li,#id_text ol li{
    color: #fff;
}
/*POLITYKA PRYWATNOSCI*/
/*COOKIES*/
#_evh-ric{
    background: #BF1313;
    border-top-color: #1c1c1c;
}
#_cmp_text{
    color: #fff;
}
/*COOKIES*/
.legal span:after{
    content: " | Ciemny motyw by Maciejozaman";
}
}
