css:
@-moz-document domain(handsontable.com) {
  body, .breadcrumbs > span {
      background-color: #151515 !important;
      color: #aaa !important;
  }
  a.link-header {
      color: #1588bc !important;
  }
  .form-control, .hot-chooser {
      background-color: #818181 !important;
      color: #000 !important;
      border-color: #1588bc !important;
  }

  table {
      background-color: #818181 !important;
      color: #000 !important;
  }

  table * th {
      background-color: #313131 !important;
      color: #d5d5d5 !important;
  }
  table * tr:nth-child(odd) {
    background-color: #a9a9a9 !important;
      color: #000 !important;
  }
  table * tr:nth-child(even) {
    background-color: #787878 !important;
      color: #000 !important;
  }

  table * a{
      color: #0134e9 !important;
      text-decoration: underline;
  }
  code.javascript {
      background-color: #000 !important;
      border-color: green;
  }

  header {
      background-color: #000 !important;
  }
  header * a {
      color: #aaa !important;
  }

  .main table .optional {
      background-color: #c6c6c6 !important;
  }
}
