css:
@-moz-document domain("ilias.studium.kit.edu") {
:root {
    --background: #222222;
    --level-1: #333333;
    --level-2: #444444;
    --level-3: #555555;
    --highlight: #777777;
    --warn: #481616;
    --text: white;
    --border: white;
}
.std {
    background-color: var(--background);
    color: var(--text);
}
.ilMainHeader {
    background-color: var(--level-1);
    border-bottom-color: var(--level-1);
}
#ilTopBarNav > li > a {
    background-color: var(--level-1) !important;
}
div.il_Block {
    background-color: var(--level-1) !important;
}
div.ilBlockInfo {
    background-color: var(--level-1);
    color: var(--level-3);
}
.ilBlockRow1,
.ilBlockRow2,
.ilBlockHeader {
    border-bottom-color: var(--level-3) !important;
}
.ilObjListRow {
    background-color: var(--level-1);
    border-bottom-color: var(--level-3);
}
.ilObjListRow:hover {
    background-color: var(--level-3) !important;
}
.btn-default,
.btn-primary {
    background-color: var(--level-2) !important;
    color: var(--text) !important;
    border: 1px solid var(--border) !important;
}
.btn-default:hover,
.btn-primary:hover {
    background-color: var(--highlight) !important;
}
.dropdown-menu {
    background-color: var(--level-2) !important;
}
.dropdown-menu li > a:hover,
.dropdown-menu li > a:focus {
    background-color: var(--highlight) !important;
    color: var(--text) !important;
}
footer.ilFooter {
    background-color: var(--level-1) !important;
}
table.calmini {
    background-color: var(--level-1) !important;
}
table.calmini tr:nth-child(2n+1) {
    background-color: var(--level-1) !important;
}
table.calmini tr:nth-child(2n) {
    background-color: var(--level-2) !important;
}
#ankuendigung > a {
    border: none !important;
}
a.ilTreeView {
    background-color: var(--level-1) !important;
    border-color: var(--level-1) !important;
    box-shadow: 1px 1px 2px var(--level-1) !important;
}
a.ilTreeView > img {
    background-color: var(--level-1) !important;
    border-color: var(--level-1) !important;
}
.ilLeftNav {
    background-color: var(--level-1) !important;
}
.jstree.jstree-default a.disabled {
    color: #666666 !important;
}
a.il_HighlightedNode,
.ilHighlighted {
    background-color: var(--highlight) !important;
    color: var(--text) !important;
}
div.ilListItemSection {
    color: var(--highlight);
}
.form-horizontal {
    background-color: var(--level-1) !important;
}
.ilFormHeader,
.ilFormFooter {
    background-color: var(--level-2) !important;
}
#f807 {
    background: var(--level-2) !important;
    border: none !important;
    box-shadow: none !important;
    text-shadow: none !important;
}
input {
    background-color: var(--level-2) !important;
}
.form-horizontal .control-label {
    color: var(--text);
}
#current_perma_link {
    color: #eeeeee;
}
#ilFrmPostList {
    background-color: var(--level-1) !important;
}
.ilToolbar.navbar {
    background-color: var(--level-1) !important;
}
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:hover,
.nav-tabs > li.active > a:focus {
    background-color: var(--highlight) !important;
    color: var(--text) !important;
}
.nav > li > a:hover,
.nav > li > a:focus {
    background-color: var(--highlight) !important;
    color: var(--text) !important;
}
div.ilTableOuter {
    background-color: var(--level-1) !important;
}
div.ilTableCommandRow,
div.ilTableCommandRowTop {
    background-color: var(--level-1) !important;
}
.ilTableOuter .table {
    background-color: var(--level-1) !important;
}
.table > thead > tr > th {
    background-color: var(--level-1) !important;
}
div.ilTableSelectAll {
    background-color: var(--level-1) !important;
}
.form-control {
    background-color: var(--level-2) !important;
    color: var(--text);
}
.ilFileUploadDropZone {
    background-color: var(--level-1) !important;
}
.ilFileUploadEntryOptions {
    background-color: var(--level-1) !important;
}
.ilFileUploadEntry {
    background-color: var(--level-2) !important;
}
.ilFileUploadEntryHeader:hover {
    background-color: var(--highlight) !important;
}
.ilFileUploadEntryProgressBack {
    background-color: var(--level-2) !important;
}
.dropdown-header {
    background-color: var(--level-3) !important;
    color: var(--text) !important;
}
div.ilc_page_cont_PageContainer {
    background-color: var(--level-1) !important;
}
div.il_droparea {
    background-color: var(--level-1) !important;
}
#ilPageEditLegend {
    background-color: var(--level-1) !important;
}
.table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: var(--level-1) !important;
}
.table-striped > tbody > tr:nth-of-type(2n) {
    background-color: var(--level-2) !important;
}
.tblstickyrow1 td {
    background-color: var(--level-3) !important;
    color: var(--text) !important;
}
.tblstickyrow2 td {
    background-color: var(--level-2) !important;
    color: var(--text) !important;
}
.ilModeratorPosting {
    background-color: var(--level-2) !important;
}
.il_VAccordionHead,
.il_HAccordionHead {
    background-color: var(--level-1) !important;
}
.ilInfoScreenSec.form-horizontal .form-group {
    background-color: var(--level-1) !important;
}
.il_InfoScreenPropertyValue {
    background-color: var(--level-1) !important;
}
.panel {
    background-color: var(--level-1) !important;
}

.ilc_text_block_Attention {
    background-color: var(--warn) !important;
}
.ilProfile {
    background-color: var(--level-1) !important;
    border: none !important;
}
.webui-popover {
    background-color: var(--level-1) !important;
}
.il-popover-title-container {
    background-color: var(--level-2) !important;
}
.il-item-task {
    background-color: var(--level-1) !important;
}
.ilc_text_block_Information {
    background-color: var(--level-3) !important;
}
}
