css:
@-moz-document domain(twitch.tv) {
body {
    background-color: #0f0e11!important;
}
* {
    font-family:'Helvetica Neue', Helvetica, Arial, sans-serif!important;
    
}

h2, h5, .chat-line__message {
    color: #d8d6db;
}
.qa-theatre-mode-button {
    position: absolute;
    bottom: 150%;
    width: 200%;
    height: 2500%;
    cursor:copy!important;
}
.tw-root--theme-dark .channel-info-bar {
    background: rgb(15, 14, 17);
    background: linear-gradient(0deg, rgba(15, 14, 17, 0) 5%, rgba(25, 23, 28, 1) 70%);
    border:none!important;
}
.player-slider__buffer {
    background: black;
}
.player-slider__left {
    background: #6441a4;
}
.pl-controls-top, .pl-controls-bottom, .player-play-overlay {
    background: none;
}
.pl-pinned-item, .player-streaminfo, .player-streamstatus {
    display: none;
}
.pl-clips-button {
    filter: invert(100%);
    position: absolute;
    right: 28px;
}
.tw-elevation-1 {
    background: #4d307c!important;
}
.tw-border-t.tw-pd-1, .tw-root--theme-dark .side-nav__overlay-wrapper {
    border-right: var(--border-width-default) solid var(--color-border-base)!important;
}
.tw-root--theme-dark .side-nav__overlay-wrapper, .tw-root--theme-dark .channel-header, .rooms-header {
    background: #1a171c;
}
.tw-border-t.tw-pd-1 {
    background: #1a171c;
    border-top:none!important;
}
.tw-root--theme-dark .channel-root__right-column, .tw-root--theme-dark .chat-room, .chat-input__textarea textarea {
    background: #18171c;
}
.tw-mg-1, .channel-header {
    border-bottom: var(--border-width-default) solid var(--color-border-base)!important;
}
h5 {
    padding-bottom: 10px;
    padding-top: 6px;
}
.chat-author__display-name{
	font-weight: bold;
}
span.chat-author__display-name {
    font-size: 12px;
    opacity: 0.8;
}
.tw-full-width.tw-justify-content-start p {
    opacity:0.7;
}
.tw-hide.tw-pd-x-2.tw-xl-flex {
    padding-left: 0rem!important;
}
.tw-align-items-center.tw-flex.tw-flex-nowrap.tw-full-height.tw-full-width.tw-justify-content-center.tw-pd-x-1 {
    opacity:0.4;
}
.top-nav__external-link.tw-interactive:nth-child(n+5), .tw-align-self-center.tw-pd-x-1:last-child {
    display:none!important;
}
.tw-hide.tw-lg-flex, .prime-offers {
    display: none !important;
}
.whispers-threads-box__container {
    opacity: 0 !important;
    transition: opacity 0.2s ease-in-out;
}
.whispers-threads-box__container:hover {
    opacity: 1 !important;
}
.channel-leaderboard {
    opacity: 0;
    height: 0rem;
    transition: opacity 0.2s ease-in-out;
}
.channel-leaderboard:hover {
    opacity: 1;
}
.tw-tower--300>* {
    width: 25%!important;
}
h1 {
    font-size: 3.5rem;
}
.navigation-link * {
    font-size: 14px !important;
}
.top-nav .tw-avatar__presence--offset {
    left: calc(86%);
    top: calc(86% - .3rem);
}
.side-nav-card__title p {
    font-size: inherit !important;
    line-height: 1.4 !important;
}
.channel-leaderboard-header__users .tw-strong {
    font-weight: normal !important;
}
.channel-leaderboard-header__users p {
    font-size: 12px;
}
.tw-textarea {
	line-height:1;
}
.tw-semibold, .tw-strong{
	font-weight: normal !important;
}
.scrollable-area {
	background-color: #18171c;
}
.tw-font-size-4 {
    font-size: 1.6rem!important;
    color: #e2e2e2;
    font-weight: normal;
}
.tw-root--theme-dark .channel-header__user{
	color: #e2e2e2;
}
.tw-c-text-base {
	color: #e2e2e2 !important;
}
.tw-font-size-5 {
	font-weight:normal;
}
.drag-and-drop-card-container.tw-border-b.tw-border-l.tw-border-r.tw-border-t.tw-c-background-base.tw-elevation-1.tw-flex.tw-flex-column.tw-lg-mg-b-2.tw-mg-b-1{
background-color:  #18171c!important;
}
}
