css:
@-moz-document url-prefix("https://clist.by/") {

body {
  background-color: #222;
  color: #aaa;
}

/****** Main Page - clist.by ******/

/**** Header ****/

.navbar-default {
  background-color: #282828;
  border-color: #333;
}

.navbar-default .navbar-nav > li > a {
  color: #aaa;
}

/*** Settings, Profile dropdown ***/

.navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:focus, .navbar-default .navbar-nav > .open > a:hover {
  background-color: #333;
  color: #aaa;
}

.dropdown-menu {
  background-color: #282828;
}

.dropdown-menu > li > a {
  color: #aaa;
}

.dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover {
  background-color: #333;
  color: #aaa;
}


/**** Body ****/


/*** List/Calendar toggle ***/
.btn-default.active, .btn-default:active, .open > .dropdown-toggle.btn-default {
  background-color: #333;
  border-color: #333;
  color: #aaa;
}

.btn-default {
  background-color: #222;
  border-color: #333;
  color: #aaa;
}

.btn-default:hover {
  background-color: #333;
  border-color: #444;
  color: #aaa;
}

/*** Search ***/
.form-control {
  background-color: #222;
  color: #ccc;
  border-color: #333;
}

.input-group-addon {
  background-color: #333;
  border-color: #333;
  color: #666;
}

/*** List ***/

#contests > div:first-child {
  border-top-color: #333;
}

#contests > div {
  border-bottom-color: #333;
  border-left-color: #333;
  border-right-color: #333;
}

.text-muted {
  color: #aaa;
}

#contests > div:hover.coming {
  background-color: #282828;
}

/** Highlighted Row **/

#contests .running {
  background-color: #002b00; /* #CFC; */
}

#contests > div:hover.running {
  background-color: #003600;
}

/** Calendar badge **/
.progress-bar-info {
  background-color: #0e3945; /* #5bc0de; */
}

.badge {
  color: #bbb;
  background-color: #333;
}

/*** Calendar ***/


/* 'Filtered long'/'Disabled fitler' toggle */
.btn-info {
  background-color: #0e3945; /* #5bc0de; */
  border-color: #165c70; /* #46b8da; */
  color: #aaa;
}

.btn-info:hover {
  background-color:#1f7e9a; /* #31b0d5; */
  border-color: #269abc;
  color: #fff;
}

/* Table */
.fc-unthemed .fc-content, .fc-unthemed .fc-divider, .fc-unthemed .fc-list-heading td, .fc-unthemed .fc-list-view, .fc-unthemed .fc-popover, .fc-unthemed .fc-row, .fc-unthemed tbody, .fc-unthemed td, .fc-unthemed th, .fc-unthemed thead {
  border-color: #333;
}

/****** Resources Page - clist.by/resources/ ******/

.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:focus, .navbar-default .navbar-nav > .active > a:hover {
  background-color: #333;
  color: #aaa;
}

.navbar-default .navbar-nav > li > a:focus, .navbar-default .navbar-nav > li > a:hover {
  color: #ccc;
}

/* Success */
.progress-bar-success {
  background-color: #265626; /* #5cb85c; */
}

/* Failure */
.progress-bar-warning {
  background-color: #81500b; /* #f0ad4e; */
}


/****** API Page - clist.by/api/ ******/

.info .text {
  background-color: #222;
  border-color: #333;
  color: #aaa;
}

.swagger-ui-wrap ul#resources li.resource div.heading h2 a:hover {
  color: #ccc;
}

/* 'GET' API line */
.swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.get div.heading {
  background-color: #282828;
  border-color: #333;
}

.swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation div.heading h3 span.path a {
  color: #aaa;
}

.swagger-ui-wrap ul#resources li.resource {
  border-bottom-color: #333;
}

.swagger-ui-wrap ul#resources li.resource div.heading ul.options li {
  border-right-color: #666;
}

}
