css:
@-moz-document domain('www.twitch.tv'), domain('clips.twitch.tv'), domain(dashboard.twitch.tv)
{
.tw-root--theme-dark {
    --color-background-base: var(--color-black);
    --color-background-alt: var(--color-black);
    --color-background-alt-2: var(--color-black);
    --color-background-interactable-alpha-hover: var(--color-opac-w-3) !important;
    --color-background-interactable-alpha-active: var(--color-opac-w-3) !important;
    --shadow-elevation-1: 0 1px 2px var(--color-opac-w-2),0 0px 2px var(--color-opac-w-2);
    --shadow-elevation-2: 0 4px 8px var(--color-opac-w-2),0 0px 4px var(--color-opac-w-2);
    --shadow-elevation-3: 0 4px 8px var(--color-opac-w-2),0 0px 4px var(--color-opac-w-2);
    --shadow-elevation-4: 0 4px 8px var(--color-opac-w-2),0 0px 4px var(--color-opac-w-2);
    --shadow-elevation-5: 0 4px 8px var(--color-opac-w-2),0 0px 4px var(--color-opac-w-2);
    --shadow-button-focus: 0 0 6px 0 var(--color-opac-w-3);
    --color-background-interactable-hover: var(--color-opac-w-3);
    --color-background-interactable-active: var(--color-opac-w-3);
 }

/***WHISPERS***/
    .tw-root--theme-dark .whispers-list-item {box-shadow:none!important;border: none!important;}
    .tw-root--theme-dark .whispers-list-item:hover {background: var(--color-opac-w-3);}
    .tw-root--theme-dark .whispers-list-item {background: var(--color-black) !important;}
    .tw-root--theme-dark .thread-header__title-bar-container {background: var(--color-black) !important;}
    .tw-root--theme-dark .thread-header__title-bar-container--focused {background: var(--color-black) !important;}
    .tw-root--theme-dark .tw-border-b {border-bottom: var(--border-width-default) solid var(--color-opac-w-3) !important;}
    .tw-root--theme-dark .whispers-list-item__archive {background-color: var(--color-black) !important;}
    .tw-root--theme-dark .whispers-list-item__unread-icon {background-color: var(--color-black) !important;}
    .tw-root--theme-dark .whispers-list-item:hover .whispers-list-item__archive-button:hover {background-color: var(--color-opac-w-3);}
    .tw-root--theme-dark .whispers-list-item:hover .whispers-list-item__archive-button {background-color: var(--color-black);}

/***NOTIFICATIONS***/
    .tw-root--theme-dark .persistent-notification__unread {background: var(--color-black); border-left: 8px solid var(--color-background-accent);}

/***CLIPS***/
    .tw-root--theme-dark .clips-top-nav-user {background: var(--color-black);}
    .tw-root--theme-dark .clmgr-table__row {background: var(--color-black);}
    .tw-root--theme-dark .clmgr-table__row:hover {background: var(--color-opac-w-3);}
    .tw-root--theme-dark .clmgr-table__row:nth-child(2n) {background: var(--color-black);}
    .tw-root--theme-dark .clmgr-table__row:nth-child(2n):hover {background: var(--color-opac-w-3);}

/***LEFT NAV***/
    .tw-root--theme-dark .side-nav-card .side-nav-card__link:hover, .recommended-friends {background: var(--color-opac-w-3);}
    .tw-root--theme-dark .side-nav__overlay-wrapper {background: var(--color-black);}
    .tw-root--theme-dark .side-nav {border-right: var(--border-width-default) solid var(--color-border-base)!important;}

/***DASHBOARD***/
    /*Header*/
    .tw-root--theme-dark .sunlight-tile {background: var(--color-opac-w-3);}
    .tw-root--theme-dark .sunlight-nav-tiles:after {background-image: var(--color-black);}
    .tw-root--theme-dark .stream-manager-panel-header {border-bottom: 1px solid var(--color-opac-w-3);}
    .tw-root--theme-dark .sunlight-help-button {box-shadow: var(--shadow-elevation-3)!important;}
    /*Quick Actions*/
    .tw-root--theme-dark .quick-actions-modal-header {background: linear-gradient(180deg,#000000 1%,transparent 1%,transparent) !important;}
    .tw-root--theme-dark .sunlight-modal-layout__transition {background: var(--color-black);}
    .tw-root--theme-dark .quick-actions-store__quick-actions-store-card {box-shadow: var(--shadow-elevation-1)!important;}
    .tw-root--theme-dark .edit-stream-info-buttons {  box-shadow: 0 -2rem 1.5rem var(--color-black);}
    /*Left Nav*/
    .tw-root--theme-dark .mosaic-window-body {background-color: var(--color-black);}
    /*Videos*/
    .tw-root--theme-dark .video-card {background: var(--color-black);}
    .tw-root--theme-dark .clip-modal.tw-c-background-base {box-shadow: var(--shadow-elevation-3)!important;}
    /*Banner*/
    .tw-root--theme-dark .tw-alert-banner--info {background-color: var(--color-twitch-purple-9);}
    /*Notification*/
    .tw-root--theme-dark .tw-notification-figure--info {color: var(--color-twitch-purple-9);}
    .tw-root--theme-dark .tw-in-feature-notification--info {border-color: var(--color-twitch-purple-9);}
    /*Main*/
    .tw-root--theme-dark .drag-and-drop-theming .mosaic-split {background: var(--color-opac-w-3);}
    .tw-root--theme-dark .drag-and-drop-theming .mosaic-split.-row {width: 1px;}
    .tw-root--theme-dark .drag-and-drop-theming .mosaic-split.-column {height: 1px;}
    .tw-root--theme-dark .stream-manager--page-view .mosaic-window-body {background-color: var(--color-black);}
    /*Channel*/
    .tw-root--theme-dark .user-image-uploader__background-container {box-shadow: var(--shadow-elevation-3);}
    .tw-root--theme-dark .update-profile-picture-modal {box-shadow: var(--shadow-elevation-3);}
    .tw-root--theme-dark .profile-edit {box-shadow: var(--shadow-elevation-3);}

    /*Achievements*/
    .tw-root--theme-dark .ach-sb {background-color: var(--color-black) !important;}
    .tw-root--theme-dark .ach-card--expanded .ach-card__inner {background-color: var(--color-black) !important;}

/***CHAT***/
    .tw-root--theme-dark .chat-viewers__pane {background: var(--color-black) !important;}
    .tw-root--theme-dark .chat-room {background: var(--color-black) !important;}
    .tw-root--theme-dark .tw-mg-y-05.user-notice-line {background-color: var(--color-black);}
    .tw-root--theme-dark .chat-line__message--special {background-color: var(--color-black);}

/***MOD VIEW***/
    .tw-root--theme-dark .user-details .mod-logs-entry-list, .user-details .viewer-card-mod-logs-page {background-color: var(--color-black)!important;}
    .tw-root--theme-dark .channel-actions__takeover-page {background-color: var(--color-black);}
    .tw-root--theme-dark .mod-view-context-bar__handle {background-color:  var(--color-black);}

/***MAIN***/
    /*Header*/
    .tw-root--theme-dark .tw-c-background-base {background: var(--color-black) !important;}
    body.dark-theme .shell-nav {background: #000000;}
    /*Channel*/
    .tw-root--theme-dark .channel-header {background: var(--color-black); border-bottom: var(--border-width-default) solid var(--color-border-base) !important;}
    .tw-root--theme-dark .channel-root__right-column {background: var(--color-black) !important;}
    .tw-root--theme-dark body {background: var(--color-black) !important;}
    .tw-root--theme-dark .channel-root__info .channel-info-content {background-color: var(--color-black) !important;}
    .tw-root--theme-dark .home-header-sticky, .home-header-sticky__inner, .home-header-sticky__wrapper {background: var(--color-black) !important;}
    .tw-root--theme-dark .stream-schedule__header {background-color: var(--color-black) !important;}
    .tw-root--theme-dark .social-media-space .tw-c-background-base {border: 1px solid var(--color-border-base);}
    .tw-root--theme-dark .stream-schedule-times {border-bottom: 4px solid var(--color-opac-w-3) !important;}
    .tw-root--theme-dark .channel-root--watch .channel-root__info {background-color: var(--color-black) !important;}
    .tw-root--theme-dark .stream-schedule-day-name__is-today {background-color: var(--color-accent) !important;}

    /*Sub*/
    .tw-root--theme-dark .sub-modal__container.tw-c-background-base {box-shadow: var(--shadow-elevation-2);}
    .tw-root--theme-dark .checkout-form-layout, .summary-layout {background-color: var(--color-black) !important;}
    .tw-root--theme-dark .sub-modal {box-shadow: var(--shadow-elevation-3);}
    .tw-root--theme-dark .mega-commerce-banner {background-color: var(--color-black);}
    /*Homepage*/
    .tw-root--theme-dark .carousel-metadata {background: var(--color-black) !important;}
    .tw-root--theme-dark .carousel-metadata--fadeout {background: var(--color-black) !important;}
    .tw-root--theme-dark .auth-modal {box-shadow: var(--shadow-elevation-2);}
    .tw-root--theme-dark .directory-header-new__banner-cover {background: var(--color-black);}
    /*User Menu*/
    label.tw-flex-grow-1:nth-child(2){visibility: hidden; position: relative;}
    label.tw-flex-grow-1:nth-child(2):after{visibility: visible; position: absolute; top: 0; left: 0; content:'Lights out'}

/***FOOTER***/
    .tw-root--theme-dark .footer {background: var(--color-black) !important;}

/***BetterTTV***/
   /*Main*/
   #bttvSettingsPanel {color: #d3d3d3; border: 1px solid #000000; background-color: #000000; box-shadow: -1px 0 0 var(--color-opac-w-3), -2px 0 0 var(--color-opac-w-3), -3px 0 0 var(--color-opac-w-3), 1px 0 0 var(--color-opac-w-3), 2px 0 0 var(--color-opac-w-3), 3px 0 0 var(--color-opac-w-3), 0 -1px 0 var(--color-opac-w-3), 0 -2px 0 var(--color-opac-w-3), 0 -3px 0 var(--color-opac-w-3), 0 1px 0 var(--color-opac-w-3), 0 2px 0 var(--color-opac-w-3), 0 3px 0 var(--color-opac-w-3);}
   #bttvSettingsPanel #header {background-color: var(--color-black); border-bottom: 1px solid var(--color-opac-w-3);}
   #bttvSettingsPanel .nav> li> a {color: var(--color-white);}
   #bttvSettingsPanel a {color: #ff0000;}
   #bttvSettingsPanel a:hover {color: #ff0000;}
   /*Settings*/
   #bttvSettingsPanel .option,
   #bttvSettingsPanel .option:nth-child(2n) {color: var(--color-text-alt-2); background-color: var(--color-black); border-bottom: 1px solid rgb(0, 0, 0) !important; box-shadow: 0px 1px 0px var(--color-opac-w-3) inset !important;}
   .bttv-switch-input:checked + .bttv-switch-label-off ~ .bttv-switch-selection {background: var(--color-opac-w-3);}
   .bttv-switch-input:checked + .bttv-switch-label-on ~ .bttv-switch-selection {background: var(--color-opac-w-3); box-shadow: inset 0 1px rgba(0,0,0, 0), 0 0 2px rgba(0,0,0, 0);}
   .bttv-switch-input:checked + .bttv-switch-label-on {color: #d3d3d3;}
   .bttv-switch {background: var(--color-opac-w-3);}
   /*About*/
   #bttvSettingsPanel #bttvAbout .aboutHalf {background-color: var(--color-black);}
   #bttvSettingsPanel #bttvAbout .aboutHalf a {color: #ff0000; opacity: 0.7; font-weight: bold;}
   /*Emote Menu*/
   .emote-menu {background-color: var(--color-black);}
   .emote-menu .header-info {border-top: 3px solid var(--color-opac-w-2);}
   .emote-menu .header-info {border-bottom: 3px solid var(--color-opac-w-2);}
   #bttvSettingsPanel h1 {color: var(--color-opac-w-12);}
   /*Footer*/
   #bttvSettingsPanel #footer {color: var(--color-opac-w-9); background-color: var(--color-black); border-top: 1px solid var(--color-opac-w-3);}
   #bttvSettingsPanel #footer a {color: #ff0000;font-weight: bold;}
}
