css:
@-moz-document url("https://shopping.yahoo.co.jp/"), url("http://shopping.yahoo.co.jp/"),
url("https://shopping.yahoo.co.jp/?sc_i=shp_pc_top_MHD_logo")
{

/*全体的に不要なもの*/
.mdImpact,
ul.comBnrLists,
div~.mdELBT,
div~.mdSidePushItems,
div~.mdEBN,
.mdIPM .elHeader,
div~.mdEPBN,
div~.mdSELBN,
.mdULT,
.mdPropertyLink,
.YshpWrFoot,
.mdPenetrateBanner,
div~.mdELBN
{
display:none!important;
}
/*中央位置にある不要なもの*/
/*ライブチャンネル*/
#lvmod{
display:none!important;
}
/*YAHOO!ショッピングからのおすすめ商品*/
#wrapper > div.YshpWrContainer > div.YshpWrContent > div > div.YshpWrCenter > div > div.uiPrOptionA{
display:none!important;
}
/*売れ筋商品*/
#popularItemPersonal{
display:none!important;
}
/*過去のお買い物*/
#ordhist{
display:none!important;
}
/*新着セール*/
.YshpWrCenter div ~ .mdDiscountList{
display:none!important;
}
/*人気ランキング*/
.YshpWrCenter div~.mdItemRanking{
display:none!important;
}
/*閲覧からのおすすめ*/
.YshpWrCenter div~.mdRowItemPersonal3{
display:none!important;
}
/*人気のクーポン*/
.YshpWrCenter div~.mdRowItemCoupon{
display:none!important;
}
/*最近チェックした商品の表示数制限*/
#viewhist > div.elItem.elMin >
ul li:nth-child(n+6){
   display:none;
}
.mdBrowseHistoryExpand.elPattern-B .elItem.elMin .elMore{
	text-align: right;
}


/*左サイドにある　テーマから探す*/
#menutm > div{
display:none!important;
}
/*右サイドにある　お知らせ*/
div~.mdSideNotice{
display:none!important;
}

/*位置調整なので触らないで下さい*/
.YshpWrRight{
margin-top:-15px;
}
}
