css:
@-moz-document domain("cgsfusion.com") {
/*论坛页*/
body {
    background: #e3e4e5;
}

.frame-1-1-r {
    background-color: #fff
}
.frame-tab .tb li a {
    background: none
}

div#tabB7u6H2_content a:hover {
    font-size: 15px;
    -webkit-transition: ease-in-out 0.25s;
    transition: ease-in-out 0.25s;
}
.chart {
    color: #65728e;
}
.footer {
    background: #fff;
}
#ft a {
    color: #65728e;
}
div#category_36,
div#category_66,
div#category_37,
div#category_70,
div#category_1 {
    background: #fff;
    padding: 20px;
}
.bmw .bm_h a {
    color: #65728e;
    font-weight: 600;
}


/*帖子页*/
#nv {
    background-color: #fff;
    opacity: 1 !important
}

#nv .nav li.a a {
    background-color: unset;
}
#nv .nav li a:hover,
#nv .nav li.hover a:hover {
    background: #57bae8;
}
#postlist.pl.bm {
    border: none;
}
.bm {
    background: #e3e4e5;
    border: none
}

.pls {
    background: unset;
    border: none;
}


#paopaokeji_roll .tbtnclass,
.rides-cs .btnOpen {
    background-color: #1693DD;
}


.qrcode_right {
    text-shadow: none;
    color: #65728e;
}

.qrcode_right a {
    color: #0071ff;
}

.aboc_box {
    background-color: #fff;
}

span.qrcode_tags a {
    color: #fff;
}

.locked {
    background: none;
}


/*门户*/
#slideshow {
    border-bottom: none;
    height: unset;
}
.post-home {
    background-color: #fff;
    box-shadow: 0 2px 20px 0 rgba(0, 41, 133, 0.2);
    border: none;
}
#post-tags:hover,
#slideshow:hover,
#index_tool,
#index_book {
    border-bottom: none;
    box-shadow: 0 2px 20px 0 rgba(0, 41, 133, 0.2);
    transform: scale(1);
}
.post-home:hover {
    border-bottom: none;
    box-shadow: 0 2px 20px 0 rgba(0, 41, 133, 0.2);
    transition: 0.45s ease;
    transform: scale(1.08);
}
.post-info,
.post-info a,
.post-noimg a,
.post-noimg p {
    color: #65728e;
    background-color: #fff;
    border: none;
}
.post-info .title {
    padding: 0;
}
.citemfoot {
    border-top: none;
}
.citemfoot span a {
    margin: 0px !important;
}
.citemfoot a.citemfimg img {
    border-radius: 50%;
}


#index_tool .title {
    background-color: #4ba6e5;
    border-bottom: none;
    padding: 10px 20px;
    color: rgba(255, 255, 255, 0.94);
    font-size: 17px;
}

.imitem {
    background: #fff;
    box-shadow: 0 2px 20px 0 rgba(0, 41, 133, 0.2);
}
.imhead {
    border: none;
}
.immore {
    border: none;
}

/*最新资源*/
.waterfall_thread {
    box-shadow: 0 2px 20px 0 rgba(0, 41, 133, 0.2);
}

.waterfall_imglink img {
    border-radius: 50%;
}

/*其他*/
.citemqt.citemfoot {
    background: #fff;
}
.ttp .a a,
.ttp a,
.ttp strong {
    background: #fff;
    border-radius: 50px;
}

h1.xs2,
span.y {
    background: #fff;
    padding-right: 10px;
    padding-left: 10px;
}
}
