css:
@-moz-document domain("digitalekunsthalle.zdf.de") {
body {
    overflow: hidden;
    padding-top: 30px;
}
body {
    padding-bottom: 0;
}
.navbar-top {
    height: 30px;
    min-height: 30px;
}
.navbar-bottom {
    height: 0;
    min-height: 0;
}
.babylonVRicon {
    bottom: 10px !important;
    background-size: auto !important;
    height: 26.8px !important;
    width: 54px !important;
}
    
.share-toggle {
    color: #fff;
    background-color: #34393f;
    height: 30px;
    width: 30px;
    display: none!important;
}
.navbar-brand {
    width: 90px;
    height: 21px;
    display: inline-flex;
    background-size: auto 31px;
    background-repeat: no-repeat;
}
.navbar-zdfk {
    display: none!important;
}

.navbar-dark .navbar-nav .nav-link {
    color: #a2a2a2;
}
}
