css:
@-moz-document domain("v3rmillion.net") {
ul.menu li a.active, td.tfoot {
    background: #7700ff!important;
}

ul.menu li ul, #footer, .panel_buttons {
    border-top: 5px solid #7700ff!important;
}
.thead {
    background: #7700ff!important;
}

.tcat, .trow_sep {
    background: #303029!important;
}

.forum_on {
	color: #7700ff!important;
}

.forum_off {
	color:white!important;
}

#bridge, .thread_head {
    border-bottom: 5px solid #7700ff!important;
}

.pagination a, a.button:link, .postbit_buttons>a:link, a.postbit_find, button, input.button {
    background: #7700ff!important;
    border: 1px solid #7700ff!important;
}
.postbit_edit, .postbit_website, .postbit_quote {
	background: #7700ff!important;
    border: 1px solid #7700ff!important;   
} 

.cRem a:visited, .cRem a:link {
    color: #7700ff!important;
}

.buttons {
    border: 1px solid #7700ff!important;
}
    
.forum active {
    color: #7700ff!important;
}
    
.pm_new, .alert_new {
    background: #7700ff!important;
}

.inline_row a:visited, .inline_row a {
    color: #7700ff!important;
}

div#container span.smalltext a span strong {
	color: #7700ff !important;
}
}

@-moz-document domain("v3rmillion.net"), domain("v3rmillion.net/siterules.php") {
a.button:visited, a.button:active, a.button:hover, .postbit_buttons>a:hover, button:hover, input.button:hover {
    background: #7700ff!important;
    border: 1px solid #7700ff!important;
}
i.fa.fa-thumbs-o-up, i.fa.fa-thumbs-o-down, i.fa.fa-search {
    filter: none!important;
}

#logo img {
    content: url(https://i.imgur.com/rnrrVzM.png) !important;
}

#peeka img {
    content: url(https://media1.tenor.com/images/aab4a4a2db887852bdb7c474dcd7a6d7/tenor.gif) !important;
}

body>.warp {
    color: #7700ff!important;
	border:1px #7700ff solid!important;
}
}
