@-moz-document domain(dict.cc) {
	
body,
td,
.orangebar {
    font-size: 16px !important;
    font-family: 'Roboto' !important;
    font-weight: 300 !important;
}
#langbar,
#entertexthere,
.subnav,
.orangebar,
input[value="X"],
.noli666 > a,
.noli666 > div[style="position:relative"],
.noli666 > .inp1,
#maincontent > table:nth-of-type(1),
img[src*="label_cm"],
.noline,
#inotherlangs {
    display: none !important;
}


.noli666 > input {
    border-radius: 10px;
    border: 5px solid darkorange !important;
}

.noli666 > input {
    margin: auto;
    /* Important */
    align-content: center;
    border: none;
}

#maincontent {
    padding-left: 50%;
    margin-left: -365px !important;
}

#maincontent > table tr:nth-of-type(1) {
    visibility: collapse;
}

td {
    min-height: 1.2em !important;
    padding-bottom: 10px;
    padding-top: 10px;
}

.td6 {
    border: none;
}

b {
    font-weight: 500;
}


