@namespace url(http://www.w3.org/1999/xhtml);
/* i really want this to be global */
html {
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    text-rendering: optimizeLegibility
}
html:lang(de) body {
    quotes: "„" "“"
}
html:lang(es) body,
html:lang(no) body {
    quotes: "«" "»"
}
html:lang(fr) body {
    quotes: "« " " »"
}
html:lang(zh-CN) body,
html:lang(zh-SG) body {
    font-family: "Sarasa Gothic SC", "方正兰亭黑_GB18030", "方正兰亭黑_GBK", -apple-system, BlinkMacSystemFont, system-ui, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Noto Color Emoji"
}
html:lang(zh-TW) body,
html:lang(zh-HK) body,
html:lang(zh-MO) body {
    font-family: "Sarasa Gothic HC", "HanHei TC", -apple-system, BlinkMacSystemFont, system-ui, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Noto Color Emoji";
    quotes: "「" "」"
}
html:lang(ja) body {
    font-family: "Sarasa Gothic J", "Hiragino Kaku Gothic Pro", -apple-system, BlinkMacSystemFont, system-ui, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Noto Color Emoji";
    quotes: "「" "」"
}
html:lang(ko) body {
    font-family: "Sarasa Gothic K", -apple-system, BlinkMacSystemFont, system-ui, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Noto Color Emoji"
}
body {
    font-family: "Sarasa Gothic SC", "方正兰亭黑_GB18030", "方正兰亭黑_GBK", -apple-system, BlinkMacSystemFont, system-ui, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Noto Color Emoji";
    quotes: "“" "”"
}
button,
input,
keygen,
select,
textarea {
    font-family: inherit
}
button:disabled,
input:disabled,
keygen:disabled,
select:disabled,
textarea:disabled {
    cursor: not-allowed
}
