css:
@-moz-document domain("lesoleil.com") {
body,
.container,
.header__secondary-header,
.full-article-basic__inner,
.full-article-basic-content__lead,
.chapter-paragraph__body {
	background: #1d1d1d !important;
	color: #fff !important;
}
img.header__secondary-header-logo-image--lesoleil {
	background:#aaa;
	padding:5px;
}
.header__search.header-search,
.header-search__title,
form.header-search__textbox,
form.header-search__textbox:focus,
input.header-search__input,
button[class^='block-newsletter-subscription__form-action'],
input[class^='block-newsletter-subscription__form-input'],
.full-article-basic__header-and-title,
.contributecampaign,
div[class^='_block-relaunch-'],
div[class^='_block-relaunch-'] div[class^='_container_'],
.full-article-basic-read-more-button__link {
	background:#333 !important;
	color:#eee !important;
	border-color:#000 !important;}

.recent-articles,
.recent-articles-article-basic__link,
.section-articles-f,
span[class^='_title_'],
span[class^='_subtitle_'],
div[class^='_subscription_'],
div[class^='_inner_'],
div[class^='_ad-'],
button[class^='_load-link_'] {
	background:#222 !important;
	color:#fff !important;
}
.xlarge-content-basic__info {
	background:rgba(0,0,0,.85) !important;
	color:#eee;
}
h2.medium-content-basic__title,
h2.popular-news-basic__title,
a[class^='_link_'] span,
.full-article-basic__title-link,
div[class^='_block-relaunch-'] div[class^='_container_'] div[class^='_title_'] {
	color:#fff !important;
}

}
