css:
@-moz-document domain("groups.io")
{
/* Default background colour and text colour */
body
{
    background-color: #4f4f4f;
    color: #ffffff;
}

/* default link colour */
a
{
    color: #5ab9e8;
}

/* Link colours in the topics/messages list */
.subject a:link
{
    color: #5ab9e8;
    font-weight: bold;
}
.subject a:hover
{
    color: #28a1dd;
}
.subject a:visited
{
    color: #8096a2;
}

/* Default title colour. used in topics/message title/etc*/
h4
{
    color: #ffffff;
}

/* Default text colour for messages */
table.table-fixed
{
    color: #f0f0f0;
}

/* Colour of reply footer section of messages */
.table-striped > tbody > tr:nth-of-type(2n+1)
{
    background-color: #3e3e3e !important;
}

/* Show Quoted Text box colour */
.hashtag-label-sage
{
    background-color: #000000 !important;
}

/* Link colour for page list. */
.pagination a:link
{
    color: #337ab7 !important;
}

/* Colour for text in various user panels. e.g. Email Delivery, Web Site Preferences, etc */
div.panel-body, div.panel-body h4
{
    color: #333333;
}

/* Color for text in box at top of some pages (e.g. the Identity page) */
div.well
{
    color: #333333 !important;
}

/* Colour for various tables (e.g. the sidebar) */
.list-group-item
{
    background-color: #acacac;
}

/* Text colour for sidebar */
a.list-group-item, button.list-group-item
{
    color: #000000;
}

/* Colouring for panels (e.g. Acount page, etc) */
.panel
{
    background-color: #acacac;
}
.panel-default > .panel-heading
{
    color: #fff;
    background-color: #2f4a64;
}
.panel-default > .panel-heading {

    background-image: -webkit-linear-gradient(top,#476786 0,#2f4a64 100%);
    background-image: -o-linear-gradient(top,#476786 0,#2f4a64 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,from(#476786),to(#2f4a64));
    background-image: linear-gradient(to bottom,#476786 0,#2f4a64 100%);
    background-repeat: repeat-x;
}
.help-block
{
    color: #ffffff;
}
.form-control
{
    background-color: #476786;
    color: #ffffff;
}

/* Changes colours for default text in textboxes. */
::-webkit-input-placeholder
{
    /* Edge */
    color: #e6e6e6 !important;
}
:-ms-input-placeholder
{ 
    /* Internet Explorer 10-11 */
    color: #e6e6e6 !important;
}
::placeholder
{
    color: #e6e6e6 !important;
}

/* Popup box text color */
.modal, .modal h4
{
    color: #000000 !important;
}

/* background colour for some topic labels */
.hashtag-label-green-lantern
{
    background-color: #005707 !important;
}

btn-sm.a:link
{
    color: #ffffff !important;
    font-weight: normal !important;
}

/* Colours the navbar at the bottom of the page. */
.navbar-default
{
    background-image: -webkit-linear-gradient(top,#cacaca 0,#cecece 100%);
    background-image: -o-linear-gradient(top,#cacaca 0,#cecece 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,from(#cacaca),to(#cecece));
    background-image: linear-gradient(to bottom,#cacaca 0,#cecece 100%);
    background-repeat: repeat-x;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 0 rgba(255,255,255,.15),0 1px 5px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 0 rgba(255,255,255,.15),0 1px 5px rgba(0,0,0,.075);
}

/* Colours database tables */
.hashtag-label-white
{
    background-color: #4f4f4f;
}

}
