css:
@-moz-document domain("horriblesubs.info") {
:root {
    --pageBackground: #121212;
    --pageForeground: #363636;
    --pageForegroundDarker: #2c2c2c;
    --pageButtonHover: #585858;
    --mainTitleColor: #da4453;
    --mainTextColor: #dcdcdc;
    --none:#00000000;
}


/*                *\
||   Main page    ||
\*                */
body {
    background-color: var(--pageBackground) !important;
    color: var(--mainTextColor);
}

/*   Navigation bar   */
.navbar.navbar-default {
    font-family: 'Open Sans', serif;
    font-weight: bold;
    font-size: 20px;
    box-shadow: 1px 1px 4px #000; 
}
.navbar-default .navbar-nav>li>a {
    background: #00000000 !important;
    font-size: 18px;
}
.navbar-nav > li {
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 0px;
    padding-bottom: 0px;
}
.navbar-default .navbar-nav > li > a {
    line-height: 70px;
}
/* Dropdown menu from IRC */
.dropdown-menu {
    background-color: var(--pageForeground);
    border-radius: 10px;
    font-size: 15px;
}
.dropdown-menu > li > a {
    color: var(--mainTextColor);
    padding-right: 45px;
}
.dropdown-menu > li > a:hover,
.dropdown-menu > li > a:focus {
    background: #00000000 !important;
}
.navbar-default .navbar-nav .dropdown-menu > li:hover,
.navbar-default .navbar-nav .dropdown-menu > li:focus {
    background: var(--pagebuttonHover);
}
.post-inner-content {
    background-color: #363636 !important;
    box-shadow: 1px 1px 4px #000;
    border-radius: 10px;
}

/*   Latest Releases   */
.latest-releases a,
.latest-show-more a {
    color: var(--mainTextColor);
}
.latest-releases .active {
    background: #475836 !important;
}
.latest-releases .active a:hover {
    background: #627351 !important;
}
.latest-releases a:hover,
.latest-show-more a:hover {
    background: #6e6e6e! important;
}
.latest-releases ul li {
    border-bottom-color: #dddddd50;
}
.latest-releases-res .badge,
.rls-label .badge {
    background-color: #646464;
}
.latest-releases a:hover .badge,
.rls-label:hover .badge {
    background-color: #494949;
}
.badge,
.rls-label:hover .badge {
    border-radius: 3px;
}
.form-control {
    background-color: var(--pageButtonHover);
    color: var(--mainTextColor);
    border: 1px solid #dddddd50;
    border-radius: 5px;
}

/* Current Time / Today's Schedule */
.well {
    background-color: #363636 !important;
    box-shadow: 1px 1px 4px #000;
    border-radius: 10px;
}
.table > tbody > tr > td,
.schedule-table > tbody>tr {
    border-bottom: 1px solid #dddddd50;
}
.table-hover > tbody > tr:hover,
.schedule-table > tbody >tr:hover {
    background-color: var(--none) !important;
}
.schedule-widget-show > a:hover,
.schedule-widget-show > a:focus {
    background: var(--none) !important;
}
/* Rounding Disqus thread and changing shadows */
#disqus_thread {
    background: var(--pageForeground) !important;
    box-shadow: 1px 1px 4px #000;
    border-radius: 10px;
    padding: 5px;
}


/*                    *\
||   Shows section    ||
\*                    */
.rls-label:hover {
    background: #6e6e6e! important;
}
a.rls-label,
.rls-info-container a:focus, .show-more a:focus,
.rls-label:hover, .entry-title{
    color:var(--mainTextColor);
}
.rls-info-container {
    border-bottom: 1px solid #dddddd50;
}
.series-image img {
    box-shadow: 2px 2px 5px #000;
    border-radius: 10px;
}
.alpha-nav-wrapper {
    border-top: 1px solid var(--pageButtonHover);
    border-bottom: 1px solid var(--pageButtonHover);
}
.alphabet-title {
    border-bottom: 1px solid var(--pageButtonHover);
}
.ind-show:hover,
.ind-show:focus {
    background: var(--none) !important;
    color: var(--mainTextColor);
}


/*                       *\
||   IRC guide section   ||
\*                       */
.navbar-default .navbar-nav .dropdown-menu > li:hover,
.navbar-default .navbar-nav .dropdown-menu > li:focus,
.dropdown-menu > .active {
    background: var(--pagebuttonHover);
}
img {
    border-radius: 10px;
}


/*                 *\
||   FAQ section   ||
\*                 */
h4.hrf-title {
    background: var(--none);
    color: var(--mainTextColor);
}
.hrf-content {
    color: var(--mainTextColor);
}


/*                          *\
||   Announcement section   ||
\*                          */
.entry-title a {
    color: var(--mainTitleColor);
}
.entry-meta a {
    color: var(--mainTextColor);
}
#secondary .widget a {
    color: var(--mainTextColor);
}
#secondary .widget a:hover {
    background: #00000000;
    color: var(--mainTitleColor);
}
.widget ul li {
    border-bottom: 1px solid var(--pageButtonHover);
}
.post-navigation a, .page-numbers,
.pagination .prev, .pagination .next {
    background-color: var(--pageForeground);
    box-shadow: 1px 1px 4px #000;
    border-radius: 5px;
    border: 1px solid var(--pageForeground);
    font-size: 15px;
}
}

@-moz-document url-prefix("https://horriblesubs.info/release-schedule/") {
/*                       *\
||   Schedule section    ||  I had to move this into a 2nd section because .post-inner-content overlaps with the 1st one.
\*                       */
:root {
    --mainTextColor: #dcdcdc;
    --none: #00000000;
}

ol, ul {
    padding-left: 50px;
}
.entry-title {
    padding-top: 10px;
}
.entry-content > p {
    margin: 0 15px;
}
.post-inner-content {
    padding: 20px;
}
.table-hover > tbody > tr:hover,
.schedule-table > tbody > tr:hover {
   background-color: var(--none) !important;
}
.table {
    width: 97%;
    margin-left: 10px;
}
.schedule-today {
    background-color: #594848;
    border-radius: 5px;
    padding-left: 10px;
}
.h1, .h2, .h3, h1, h2, h3 {
    padding-left: 10px;
}
a:hover, a:focus {
    color: var(--mainTextColor);
}
}

@-moz-document url-prefix("https://free.timeanddate.com/clock/i4vybhfo/n137/fn17/th1/ta1") {
/* Clock */
#c1 {
    background-color: #00000000;
}
#t1c,#t1 {
    color: #dcdcdc;
}
}

@-moz-document url-prefix("https://disqus.com/embed/comments/?base=default&f=horriblesubs") {
/* Disqus comment section */
:root {
    --pageBackground: #121212;
    --pageForeground: #363636;
    --pageForegroundDarker: #2c2c2c;
    --pageButtonHover: #585858;
    --mainTitleColor: #da4453;
    --mainTextColor: #dcdcdc;
    --none: #00000000;
}

body {
    background: var(--pageForeground) !important;
}

/* Highlighted post */
.highlighted-post .highlighted .post-content,
.highlighted > .post-content{
    background-color: var(--pageForegroundDarker);
    box-shadow: 1px 1px 4px #000; 
    border-radius: 10px;
}
.highlighted-comment-header {
    color: var(--mainTitleColor);
    font-size: 20px;
    padding-left: 18px;
}
.highlighted-post .highlighted {
    background-color: var(--pageForeground);
}
.highlighted-post .post-list {
    margin-bottom: 0px;
}
.highlighted > .post-content {
    margin: 0 16px 24px 16px;
}
.avatar .user, .avatar img {
    border-radius: 5px;
}
h3, h4, strong, strong a {
    color: var(--mainTextColor)
}
.comment__footer .post-byline .has-edit, .comment__footer .post-byline .parent-link,
.comment__footer .post-byline .state-byline, .comment__footer .post-byline .time-ago,
.comment__footer .post-meta .has-edit, .comment__footer .post-meta .parent-link,
.comment__footer .post-meta .state-byline, .comment__footer .post-meta .time-ago {
    color: var(--mainTextColor)
}
.comment__header .post-byline .state-byline, .comment__header .post-byline .time-ago,
.comment__header .post-meta .has-edit, .comment__header .post-meta .parent-link,
.comment__header .post-meta .state-byline, .comment__header .post-meta .time-ago {
    color: #dddddd80;
}
.post-votes .vote-down .count,
.post-votes .vote-up .count {
    color: #dddddd80;
}
.comment__footer .vote-down, .comment__footer a,
.comment__header .vote-down, .comment__header a {
    color: #dddddd80;
}
.comment__footer .vote-down:hover, .comment__footer a:hover,
.comment__header .vote-down:hover, .comment__header a:hover {
    color: var(--mainTextColor);
}
.upvoters-outer .upvoters .scroll-measure {
    background:var(--pageButtonHover)
}
.upvoters-outer .upvoters .user {
    color:var(--mainTextColor);
}
.upvoters-outer:before,
.upvoters-outer:after {
    background-image: var(--pageButtonHover);
}
.tooltip-point:after,
.tooltip-point:before {
    background: var(--pageButtonHover);
    box-shadow: 1px 1px 4px #000; 
}
.upvoters-outer .upvoters .user .username:hover {
    color:#fff;
}
a, abbr, acronym, address, blockquote, button, caption, cite, code, dd, del, dfn, dl, dt, em, fieldset, form, h1, h2, h3, h4, h5, h6, img, label, legend, li, ol, p, pre, q, s, samp, small, strike, strong, sub, sup, table, tbody, td, tfoot, th, thead, tr, tt, ul, var {
    color: var(--mainTextColor);
}
.nav-primary {
    border-bottom: 2px solid #dddddd50;
    border-radius: 0px;
    padding: 0 16px 0 16px;
}
.nav-secondary {
    padding: 0 16px 0 16px;
}
.dropdown-toggle {
    color: #dddddd80 !important;
}
.dropdown-toggle:hover {
    color: var(--mainTextColor) !important;
}
.dropdown-menu {
    background:var(--pageButtonHover);
    box-shadow: 1px 1px 4px #000; 
    border-radius:10px;
    border:0px;
}
.dropdown-menu a {
    color: var(--mainTextColor);
}
.dropdown-menu li>a:hover {
    background: var(--none);
}
.caret {
    border-top: 4px solid #dddddd80;
}
.recommend .label {
    color: var(--mainTextColor);
}
.thread-likes .label-count {
    border-radius:5px;
    background:var(--pageButtonHover)
}
.thread-likes a:hover .label-count {
    color: #fff;
}
.icon-cog:hover, .icon-export:hover, .label:hover {
    color: var(--mainTextColor);
}
.tab-conversation.active>a {
    color: var(--mainTextColor) !important;
}
.textarea-outer-wrapper--top-level .avatar {
    padding-left: 16px;
}
.textarea-outer-wrapper .textarea-wrapper {
    background: var(--pageForegroundDarker);
    border: 1px solid #dddddd50;
    margin: 0 16px 0 16px;
}
.placeholder {
    color: #dddddd50;
}
.connect {
    padding: 0 22px 0 16px;
}
.tooltip {
    background: var(--pageButtonHover);
    box-shadow: 1px 1px 4px #000;
    color: var(--mainTextColor)
}
.help-tooltip__heading,
.help-tooltip__list li,
.help-tooltip__list li span {
    color: var(--mainTextColor);
}
.input--text {
    background: var(--pageForegroundDarker);
    border: 1px solid #dddddd50;
}
.post-actions {
    background: var(--pageButtonHover);
}
.wysiwyg__item {
    opacity: 1;
}
.help-icon {
    border: 2px solid var(--pageButtonHover);
}
.help-icon:before {
    color: var(--pageButtonHover);
}
.postbox section label {
    color: #dddddd50;
}
.post-content {
    padding: 0 16px 0 16px;
}
p.flagging__subtitle,
.moderate p {
    color:var(--mainTextColor)
}
p.flagging__reason-subtext {
    color:#687a86;
}
.moderate {
    background:var(--pageForegroundDarker)
}
.post-content.new, .post-content.target {
    padding-left: 16px;
}
.post-content .post-menu>li a {
    color: var(--mainTextColor) !important;
}
.post-content .post-menu .collapse a:hover,
.post-content .post-menu .expand a:hover {
    color: var(--mainTextColor);
}
.guest {
    padding: 2px 16px 0;
}
.tooltip__content h3 a {
    color:#fff;
}
.tooltip__footer {
    background: var(--pageButtonHover);
}
.follow-btn.private {
    background-color:var(--pageForegroundDarker) !important
}
.full-profile {
    color:var(--mainTextColor)
}
a:hover {
    color:#fff;
}
button.alert {
    margin-left: 16px;
    width: 95.5%
}
.alert {
    padding-right:15px;
}
.load-more {
    padding: 0 16px 0 16px;
}
.btn {
    border-radius: 5px;
    color: var(--mainTextColor);
    background: rgba(88, 99, 106,1);
}
.btn:not(:disabled):hover {
    background: rgba(88, 99, 106,1);
    color: var(--mainTextColor);
}
.disqus-footer__wrapper {
    border-top: 2px solid #dddddd50;
    padding: 16px 16px 0 16px;
}
.disqus-footer__link {
    color: #dddddd80;
}
.email a:hover, .install a:hover, .privacy a:hover {
    color: var(--mainTextColor);
}
}
