    border: 1px solid #424242;
    border-left: 2px solid #424242;
    padding: 6px;
    font-size: 12px;
    margin-top: 6px;
}

.node+.node {
    border-top: 1px solid #424242;
}

.menu-footer {
    padding: 6px 15px;
    font-size: 12px;
    color: #c2c2da;
    background: #212121;
    border-top: 1px solid #2a4d63;
