}

.menu-arrow {
    position: absolute;
    width: 0;
    height: 0;
    border: 0 solid transparent;
    top: -8px;
    left: 50%;
    margin-left: -8px;
    border: 9px solid transparent;
    border-top-width: 0;
    border-bottom-color: #FFF;
}

.menu-content {
    margin: 0;
    padding: 0;
    list-style: none;
    text-align: left;
    font-size: 13px;
    color: #c2c2da;
