}

.block--messages .message, .block--messages .block-row {
    color: #c2c2da;
    background: #424242;
    border-width: 1px;
    border-style: solid;
    border-top-color: #424242;
    border-right-color: #424242;
    border-bottom-color: #424242;
    border-left-color: #424242;
    border-radius: 4px;
}

.block-container {
    color: #c2c2da;
    background: #424242;
    border-width: 1px;
    border-style: solid;
    border-top-color: #424242;
    border-right-color: #424242;
    border-bottom-color: #424242;
    border-left-color: #424242;
    border-radius: 4px;
}

.message-cell.message-cell--user, .message-cell.message-cell--action {
    position: relative;
    background: #424242;
    border-right: 1px solid #424242;
    min-width: 0;
}

.message-userArrow:after {
    position: absolute;
    top: -9px;
    right: -10px;
    content: "";
    border: 9px solid transparent;
    border-left-width: 0;
    border-right-color: #424242;
}

.message-signature {
    margin-top: 10px;
    font-size: 12px;
    color: #424242;
    border-top: 1px solid #424242;
    padding-top: 3px;
}

.message-attribution {
    color: #a5a5af;
    font-size: 12px;
    padding-bottom: 3px;
    border-bottom: 1px solid #424242;
}

.block-body {
    list-style: none;
    margin: 0;
    padding: 0;
    background: #212121;
}

.message .reactionsBar {
    margin-top: 10px;
    padding: 6px;
    background: transparent;
}

.p-header {
    text-decoration: none;
    background: #111;
 /* background-image: url(https://i.imgur.com/E1GsUX9.png); */
    background-repeat: repeat-x;
}

element.style {
    /* text-align: center; */
}

.fr-box.fr-basic .fr-wrapper {
    background: #424242;
    border: 1px solid #212121;
    border-top: 0;
    top: 0;
    left: 0;
}

.fr-toolbar {
    color: #fffff;
    background: #212121;
    position: relative;
    z-index: 4;
    font-family: 'Helvetica Neue',Helvetica,Arial,sans-serif;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    user-select: none;
    -o-user-select: none;
    -moz-user-select: none;
    -khtml-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    padding: 0 1px;
    border-radius: 2px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    text-align: left;
    border: 1px solid #424242;
    border-top: 1px solid #424242;
    text-rendering: optimizelegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    line-height: 1.2;
}

.pageNav-jump {
    display: inline-block;
    background: #212121;
    color: #c2c2da;
    border-width: 1px;
    border-style: solid;
    border-top-color: #424242;
    border-right-color: #424242;
    border-bottom-color: #424242;
    border-left-color: #424242;
    font-size: 13px;
    white-space: nowrap;
    border-radius: 2px;
    padding: 5px 8px;
}

.pageNav-page {
    display: table-cell;
    background: #212121;
    color: #c2c2da;
    border-width: 1px;
    border-style: solid;
    border-top-color: #424242;
    border-right-color: #424242;
    border-bottom-color: #424242;
    border-left-color: #424242;
    font-size: 13px;
    white-space: nowrap;
}

.bbCodeBlock {
    margin: .75em 0;
    background: #424242;
    border: 1px solid #212121;
    border-left: 3px solid #363636;
}

.bbCodeBlock-title {
    padding: 6px 10px;
    font-size: 13px;
    color: #00b894;
    background: #2d3436;
}

.bbCodeBlock-expandLink {
    display: none;
    position: absolute;
    top: 87px;
    left: 0;
    right: 0;
    height: 75px;
    cursor: pointer;
    z-index: 100;
    background: transparent;
    background: linear-gradient(to bottom, rgba(36,59,77,0) 0%, #2d3436 80%);
}

.input {
    font-size: 15px;
    color: #c2c2da;
    background: #424242;
    border-width: 1px;
    border-style: solid;
    border-top-color: #212121;
    border-right-color: #212121;
    border-bottom-color: #212121;
    border-left-color: #212121;
    border-radius: 4px;
    padding: 6px;
    display: block;
    width: 100%;
    line-height: 1.4;
    text-align: left;
    word-wrap: break-word;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
}

.menu-separator {
    margin: 0 7.5px;
    padding: 0;
    border: none;
    border-top: 1px solid #424242;
}

.menu-footer {
    padding: 6px 15px;
    font-size: 12px;
    color: #c2c2da;
    background: #212121;
    border-top: 1px solid #424242;
    /* background: linear-gradient(180deg, #243b4d, #203c47); */
}

.menu-header {
    padding: 6px 15px;
