    border-bottom: 1px solid #212121;
    background: #2d3436;
}

.menu-row.menu-row--separated+.menu-row {
    border-top: 1px solid #424242;
}

.menu-content {
    margin: 0;
    padding: 0;
    list-style: none;
    text-align: left;
    font-size: 13px;
    color: #c2c2da;
    background: #212121;
    border-radius: 3px;
    border-top: 3px solid #0e242d;
}

.blockLink:hover {
    background: #424242;
    text-decoration: inherit;
}

.blockLink.is-selected {
    font-weight: 700;
    background: #424242;
    border-left: 3px solid #2d3436;
    padding-left: 7px;
}

::-webkit-scrollbar-thumb {
    background: #424242;
}

::-webkit-scrollbar-track {
    background: #212121;
}

.memberTooltip-header {
    display: table;
    table-layout: fixed;
    width: 100%;
    padding: 6px;
    background: #212121;
    border-bottom: 1px solid #424242;
}

.memberTooltip-separator {
    margin: -1px 6px 0;
    border: none;
    border-top: 1px solid #424242;
}

.structItemContainer-group--sticky {
    background: #424242;
}

.structItem {
    display: table;
    table-layout: fixed;
    border-collapse: collapse;
    border-top: 1px solid #424242;
    list-style: none;
    margin: 0;
    padding: 0;
    width: 100%;
}

.block-filterBar {
