/* ==UserStyle==
@name         W3Schools Sublime Theme (Fixed)
@description  Dark style for W3Schools. Created by LeoTM, updated by Izheil.
@namespace    https://github.com/Izheil/Dark-userstyles
@version      1.4.5
@author       LeoTM, Izheil
@homepageURL  https://github.com/Izheil/Dark-userstyles/tree/master/Specific%20site%20themes
@supportURL   https://github.com/Izheil/Dark-userstyles/tree/master/Specific%20site%20themes
@license      BY-SA-4.0
@preprocessor stylus
==/UserStyle== */

@-moz-document url-prefix("https://www.w3schools.com/") {
/* 'w3schools' */
body > div.w3-container.top > a {
    color: #F8F8F2 !important;
}
/* '.com' */
body > div.w3-container.top > a > span {
    color: #A6E22E;
}
@media (min-width: 993px) {
    div.w3-right {
        color: #F8F8F2 !important;
    }
}

/* Site bg + inner tables */
body > div.w3-container.top,
.w3-white, .w3-code.notranslate,
.w3-code.w3-border.notranslate,
.w3-row, #entercolor, .howtocontainer {
    background-color: #36372f !important;
    color: #ccc !important;
}
td {
    background-color: #36372f;
}

.w3-table-all th, .browserref th {background-color: #272822 !important}

.w3-table-all tr:nth-child(2n+1) td {background: #46473f !important}

/* Left sidebar bg + outer tables */
.w3-light-grey, .w3-example,
.w3-sidenav, .w3-container.w3-padding-large,
.w3-card-4 {
    background-color: #272822 !important;
}
#leftmenuinnerinner > a:hover {
    color: #F8F8F2 !important;
    background-color: #1b1c17;
}
#leftmenuinnerinner > a.active {
    color: #F8F8F2 !important;
    background-color: #1b1c17;
}

/* Navbar bg */
#topnav > div:nth-child(1),
.w3-topnav {
    background-color: #1b1c17 !important;
}
/* Navbar hover bg */
#topnav > div:nth-child(1) > div > a.active,
.topnavlinks > a:hover {
    background-color: black;
}

/* Links sidebar hover */
a.active_overview, .tut_overview {
    background-color: #1b1c17 !important}

/* Site text + in tables + buttons */
div#main > p, td, th, li, [id*="color"],
#main > div > a.w3-btn,
a.w3-btn.w3-theme.w3-margin-0,
div#topnav > div > ul.w3-navbar.w3-left > li > a.active,
body > div.w3-container.top > div,
#main > div > div:not(.w3-code) > span,
.w3-code.notranslate,
a.w3-btn.w3-light-grey.w3-hide-small.w3-hide-medium.w3-left.w3-padding,
#main > div > span:nth-child(1),
.w3-text-dark-grey, .w3-hover-text-dark-grey:hover, .w3-text-dark-gray, .w3-hover-text-dark-gray:hover,.w3-light-grey, .w3-hover-light-grey:hover, .w3-light-gray, .w3-hover-light-gray:hover {
    color: #F8F8F2 !important;
}
/* Headings (beige) */
span, h1, h2, h3, h4, h5, h6,
#sidemenu > h2 {
    color: #E6DB74;
}
/* Table text */
td, #main > div.w3-example > p {
    color: #F8F8F2;
}
/* Prevus + Next btns */
#main > div > a.w3-btn {
    background: #4CAF50;
    border: none;
}

.howtopag_item:not(.pagactive):hover {background: rgba(0,0,0,0.2) !important}

/* Links */
a:not(.w3-btn):not(.w3-button):not(.pagactive), .w3-bar-block .w3-bar-item { color: #66D9EF !important; }
a:not(.w3-btn):not(.w3-button):not(.pagactive):visited, .w3-bar-block .w3-bar-item:hover { color: #139db9 !important; }
a:not(.w3-btn):not(.w3-button):not(.pagactive):not(.active):not(.tabactive):hover { color: #A6E22E !important; }
body > ul.w3-navbar.w3-theme.w3-card-2.w3-wide > li > a.w3-hover-white.w3-padding-16 {
    color: #fff !important;
}

/* Table links (dark blue) */
#main > table > tbody > tr > td > a:not(.w3-btn) {
    color: #16b1d0 !important;
}
/* Table links (dark blue) */
#main > table > tbody > tr > td > a:not(.w3-btn):visited {
    color: #1189a2 !important;
}

#leftmenuinnerinner > a {
    color: red;
}

/* Editor  */
div#tryitLeaderboard,
div#container > div#textareacontainer,
div.CodeMirror.cm-s-default.CodeMirror-wrap,
div#iframecontainer > div#iframe,
iframe#iframeResult {
    background-color: #36372f !important;
}

span.cm-tag, .w3-code span[style="color:brown"] {
    color:  #ff8c1a !important;
}
span.cm-tag.cm-bracket, .w3-text-indigo, .w3-hover-text-indigo:hover {
    color:  #4CAF50 !important;
}
span.cm-string {
    color: #9999ff !important;
}
span.cm-variable, span.cm-def {
    color: #a6a6a6 !important;
}
span.cm-property {
    color:  #ff6666 !important;
}

/* White buttons */
.w3-btn.w3-white {
    background-color: #272822 !important;
    color: #ccc !important;
    border-color: rgba(255,255,255,0.1) !important;
}

.w3-btn.w3-white:hover {
    background-color: #1b1c17 !important;
    color: #fff !important;
}

/* Other fixes */

/* Missing text colors */
#footer .w3-center.w3-small.w3-opacity, .gs-webResult .gs-snippet, 
.gs-imageResult .gs-snippet, .gs-fileFormatType, .notranslate p, 
.exercisewindow p, .exercisewindow pre, #main, .exerciseprecontainer, 
.w3-button.w3-light-grey:hover, .goog-te-banner-content, .cm-meta, 
.w3-button.w3-dark-grey:hover, .accordion {color: #ccc !important}

.w3-info li, .w3-warning li {color: #000 !important}

span[style="color:mediumblue"], .cm-m-javascript.cm-keyword, 
.cm-m-javascript.cm-atom, .gsc-cursor-current-page {color: #0af !important}
span[style="color:black"] {color: #ddd !important}

/* Missing Button and menu-button backgrounds */
#leftmenuinnerinner a:hover, #leftmenuinnerinner .activesub, 
#leftmenuinnerinner .ref_overview, .gsc-modal-background-image, 
.exercisewindow, .w3-codespan, .w3-dark-grey, .w3-button.w3-light-grey:hover,
#leftmenuinnerinner a:focus, .w3-button.w3-bar-item:hover, 
.w3-button.w3-dark-grey:hover, .tablinks:not(.tabactive):not(.active):hover {background: rgba(0,0,0,0.5) !important}

/* Info and note containers */
.w3-info, .w3-container.w3-light-gray {
    background: rgba(0,100,70,0.3) !important;
    color: #fff !important} 

.w3-note, .w3-sand  {
    background: rgba(150,150,0,0.3) !important;
    color: #fff !important} 

/* Other containers and some headers */
 #w3-exerciseform, .trytopnav, ul.tab, .showpanel {
     background-color: #272822 !important}

.howto-container, .sidenav, .accordion, .w3-card-4 .searchbar, 
.w3-container .topnav3 {
    background-color: #1b1c17 !important}

.w3-card-4 .searchbar a:hover, #myTopnav a:hover,
.w3-container .topnav a:not(.active):hover,
.topnav2 a:not(.active):hover,
.w3-container .topnav3 a:not(.active):hover {background-color: #000 !important}

iframe {border-color: #000 !important}

/* Small fix for active link */
#leftmenuinnerinner a.active, .tablinks.active, 
.tabactive  {background: #055 !important}

/* Green butons on hover */
.w3-button.w3-green:hover {
    background: #3c8f30 !important;
    color: #fff !important}

.w3-button.w3-green:active {
    background: #2c6f20 !important}

/* Elements with incorrect borders */
#leftmenuinnerinner .searchinput {
    background-color: #1b1c17 !important;
    border-color: #055 !important}

hr, #main, .w3-bordered tr, .w3-table-all tr, 
.w3-table-all, .w3-bordered {border-color: #56574f !important}

/* Google inputs */
 #gsc-i-id1, #gsc-iw-id1, 
 .gsc-results-wrapper-overlay, .gsc-webResult.gsc-result, 
 .gsc-results .gsc-imageResult, .exerciseprecontainer,
 .goog-te-gadget-simple, .goog-te-menu2, .goog-te-menu2 td, 
 .goog-te-menu2-item div:not(:hover) {
     background-color: #36372f !important}

.gsc-webResult.gsc-result, .gsc-results .gsc-imageResult, 
.goog-te-gadget-simple {border-color: #36372f !important}

/* Google services blurred edges */
.goog-te-menu-frame {box-shadow: 0 3px 8px 2px rgba(0,0,0,0.5) !important}

/* Fix for other widgets white background */
#googleSearch, .gsc-search-box tbody td, .gsc-cursor-page, 
#google_translate_element, .tab {background-color: transparent !important}

/* Warning banners */
.w3-warning {
    background-color: #733 !important;
    color: #ddd !important;
}
}
