css:
@-moz-document url("https://catwar.su/cw3/") {
/*полосы прокрутки*/
body::-webkit-scrollbar,#fightLog::-webkit-scrollbar {
    width:0;}
#text::-webkit-scrollbar {
    width:15px;height:10px;}
#family::-webkit-scrollbar,#history::-webkit-scrollbar,#dein::-webkit-scrollbar,#chat_msg::-webkit-scrollbar,#itemList::-webkit-scrollbar,#akten::-webkit-scrollbar {
    width:15px;}
#text::-webkit-scrollbar-track,#family::-webkit-scrollbar-track,#history::-webkit-scrollbar-track,#dein::-webkit-scrollbar-track,#chat_msg::-webkit-scrollbar-track,#itemList::-webkit-scrollbar-track,#akten::-webkit-scrollbar-track {
    background:var(--bg-color0)!important;
    border-radius:10px;}
#text::-webkit-scrollbar-thumb,#family::-webkit-scrollbar-thumb,#history::-webkit-scrollbar-thumb,#dein::-webkit-scrollbar-thumb,#chat_msg::-webkit-scrollbar-thumb,#itemList::-webkit-scrollbar-thumb,#akten::-webkit-scrollbar-thumb {
    background:var(--bg-color5)!important;
    border:0.5px solid var(--bg-color4)!important;
    border-radius:10px;}
#text::-webkit-scrollbar-corner,#family::-webkit-scrollbar-corner,#history::-webkit-scrollbar-corner,#dein::-webkit-scrollbar-corner,#chat_msg::-webkit-scrollbar-corner,#itemList::-webkit-scrollbar-corner,#akten::-webkit-scrollbar-corner {
  background:transparent;}
#chat_msg,#akten,#dein,#itemList,#history,#family {
    overflow-y:auto;
    overflow-x:hidden;}

/*цветовая палитра*/
html {
    --bg-color0:#111111;
    --bg-color1:#191919;
    --bg-color2:rgba(34,34,34,/*[[tr_style]]*/);
    --bg-color3:rgba(45,45,45,/*[[tr_style]]*/);
    --bg-color4:rgba(40,40,40,/*[[tr_style]]*/);
    --bg-color5:#3c3c3c;
    --bg-color6:rgba(188,188,188,/*[[tr_style]]*/);
    --bg-color7:#2d2d2d;
    --tx-color0:#000000;
    --tx-color1:#838383;
    --tx-color2:#b2b2b2;
    --tx-color3:#ababab;
    --bg-fight1:#482419;
    --bg-fight2:#353535;}

/*всплывающие окна*/
#reveal {
    background:var(--bg-color4) url(http://d.zaix.ru/frqx.png) no-repeat -200px -80px!important;}
#reveal_info>div>table>tbody {
    border:.5px solid var(--tx-color1)!important;}

/*выделение*/
::selection {
    background-color:var(--tx-color3);}
span:focus {
    outline:0;}
#text:focus,#msg_send:focus,#mit:focus,#mitok:focus,#do_exchange:focus,#rabbit_my:focus,#rabbit_his:focus {
    outline: 2px solid var(--tx-color1)!important;
    outline-offset:-1px;}

/*ссылки и линии*/
a {
    color:var(--tx-color2);
    text-decoration:none;}
a:hover {
    color:var(--tx-color2);
    text-decoration:underline;}
hr {
    border: none;
    background-color:var(--bg-color2);
    color:var(--bg-color4);
    height:2px;}

/*изображение загрузки*/
#chat_msg_loading,#family_block>img,#pairs-loading {
   content:url(http://d.zaix.ru/hgx3.gif);} 

/*[[cwshed_style]]*/

/*мой кот, чат, лс*/
.small {
    font-size:1em;
    left:0;
    text-align:center;
    top:8px;
    position:fixed;
    width:230px;
    z-index:1;}
#newchat,#newls {
    background-color:transparent!important;
    color:var(--bg-color6)!important;
    font-size:.7em;
    vertical-align:super;}
.small>a {
    text-decoration:none;}

/*тело и тб*/
body {
    color:var(--tx-color1)!important;
    height:1080px;
    margin-left:auto;
    margin-right:auto;
    max-width:1920px;
    position:relative;
    overflow-x:hidden;}
body>#app>p:not([id]) {
    left:-300px;
    text-align:center;
    top:825px;
    position:relative;}
body:first-line {
    color:var(--tx-color2);}

/*поле игровой*/
#tr_field {
    border:3px solid var(--bg-color1);
    left:160px;
    position:absolute;
    top:-6.4%;
    transform: scale(0.863);
    z-index:3;}

/*убирает погодную тусклость игровой*/
#cages_div {
    opacity:1!important;}

/*цвет шрифта в игровой*/
.move_name,.cat_tooltip,.cat_tooltip>u>a,.cat_tooltip>.online,.cat_tooltip>a {
    color:var(--tx-color0)!important;}

/*блок чата*/
input {
    background-color:var(--bg-color0);
    border-radius:5px;
    border:.5px solid var(--bg-color4);
    color:var(--tx-color2) !important;}
select {
    background-color:var(--bg-color0);
    color:var(--tx-color1);}
#tr_chat {
    background-color:var(--bg-color3);
    border:2px solid var(--bg-color1);
    position:absolute;
    right:0;
    top:0;
    width:42.75%;}
#chat_msg {
    height:253px;
    max-width:77.2%;
    padding:0px 10px 12px 15px;}
#chat_msg>span>table>tbody>tr>td>a {
    text-decoration:none;}
#txt {
    max-height:53px;
    overflow-x:inherit;}
#text {
    max-width:735px!important;
    max-height:53px;
    overflow-x:inherit;
    overflow-y:auto;}
input#text {
    height:15px!important;}
#chat_form>span>b {
    display:none;}
#chat_form>span>b {
    display:none;}
#chat_form{
    height:35.5px;
    margin: 15px 15px 0px 15px;
    padding: 0 0 22px 0;}
.chat_text {
    width: 738px!important;}
.nick {
    border-bottom:1px dotted var(--tx-color2);
    color:var(--tx-color2) !important;}
.myname {
    background-color:var(--bg-color6)!important;
    border-radius:5px;
    color:var(--tx-color0);
    padding: 3px;}

/*ползунки*/
.ui-slider {
    background:var(--bg-color0);
    border:.5px solid var(--bg-color4);
    margin-right:50px;
    right:5px;
    top:10px;
    z-index:0;}
.ui-slider .ui-slider-handle {
    background:var(--bg-color5);
    border:.5px solid var(--bg-color4);}
#layer {
    left:2.5%;}

/*блок действий*/
#block_mess {
    background-color:var(--bg-color2);
    border:2px solid var(--bg-color1);
    height:6.7%;
    line-height:72px;
    position:absolute;
    right:0;
    text-indent:13px;
    top:31.1%;
    width:42.75%;}
#block_deys {
    border:2px solid var(--bg-color1);
    font-size:0;
    min-height:6.7%;
    position:absolute;
    right:0;
    top:31.85%;
    width:42.75%;
    z-index:2;}
#akten,#dein {
    margin-left:5px;
    max-height: 70px!important;
    max-width: 550px!important;}
#tr_actions {
    background-color:var(--bg-color2);
    border:0;}
#mit {
    background-color:var(--bg-color0);
    border-radius:5px;
    border:.5px solid var(--bg-color4);
    color:var(--tx-color1);
    font-size:14px;
    margin-top:15px;
    max-width:235px;}
#mitok {
    background-color:var(--bg-color0);
    border-radius:5px;
    border:.5px solid var(--bg-color4);
    color:var(--tx-color1);
    font-size:14px;
    margin-top:3px;}

/*блок предметов*/
#tr_mouth {
    background-color:var(--bg-color2);
    border:2px solid var(--bg-color1);
    min-height:114px;
    position:absolute;
    right:0;
    top:38.7%;
    width:42.75%;
    z-index:1;}
#tr_mouth>td>a,#tr_mouth>td>span {
    margin-left:11px;}
#thdey {
    padding: 5px 0 0 5px;}
#ctdey {
    padding:5px 0 10px 8px;}
#tir {
    margin-left:8px;}
#itemList{
    max-height:73px!important;}
#katze>td>i {
   font-size:0em;}
.active_thing {
    border-radius:8px;
    border:none;
    box-shadow:0 0 0 1px var(--tx-color1) inset;}

/*блок истории*/
#history {
    background-color:var(--bg-color2);
    border:2px solid var(--bg-color1);
    max-height:29.7%;
    padding: 5px 10px 5px 10px;
    position:absolute;
    right:17.1%;
    top:49.4%;
    width:24.6%;}
#history_clean {
    margin-left:34%;
    position:absolute;}
#ist {
    color:var(--tx-color1);}
#history {
    color:transparent;}
#history_block>div {
    background-color:transparent!important;}

/*блок родственных связей*/
#family {
    background-color:var(--bg-color2);
    border:2px solid var(--bg-color1);
    max-height:29.4%;
    padding: 5px 10px 8px 10px;
    position:absolute;
    right:0;
    top:49.4%;
    width:15.95%;}

/*панель боя*/
#fightPanel {
    background-color:var(--bg-color7);
    border-radius:0;
    border:2px solid var(--bg-color1);
    height:30%;
    position:absolute;
    right:0;
    top:49.4%;
    width:16.54%!important;
    z-index:4;}
#fightLog {
    height:92%!important;
    margin-right:5px;}
.hotkey {
    background-color:var(--bg-color0);
    color:var(--tx-color1) !important;
    margin-bottom:2px;}
.log_claws {
    color:var(--bg-fight1)!important;}
.log_teeth {
    color:var(--bg-fight2)!important;}

/*кнопка возврата поля после нюха*/
#tr_tos input {
    position:absolute;
    right:26%;
    top:4.3%;
    z-index:1;}

/*убирает заглавия блоков, небо*/
#history>h2,#family>h2,#parameters.toggle,.title,#tr_sky {
    display:none;}

/*убирает оставшийся под блоками мусор*/
#main_table {
    background:none!important;}
#info_main {
    background-color:var(--bg-color2);}
#site_table {
    background-color:transparent;}

/*дополнительно*/
#history_block>hr {margin-top:25px;background-color:var(--bg-color1)!important;} #history_block>h2 {display:none;} #erase_attacker {margin-left:26%;} #attacker_block {color:var(--tx-color1);} #cleaner_block {color:var(--tx-color1);} #erase_cleaner {margin-left:29%;}.small,p {background:transparent!important;} #fteams-wrap {position:absolute;width:320px!important;top:70%;} .reload,.alert {z-index:5!important;}

