css:
@-moz-document domain("biologiepagina.nl"), domain("") {
body {
	background-color: #303030 !important;
	background-image: none !important;
    color: #FFF; /*!important;*/
    margin: 0px !important
}

a {
    color: #4eaeff !important
}

table, img {
	background-color: #404040 !important;
    border-radius: 10px;
}

div {
    margin: 5px 0px 5px 5px;
}

.style8, .style9 {
    margin: 5px 0px 5px 0px !important;
}

h1, h2, h3, h4, .style10 {
    color: #73bdf2 !important
}

#sidebar, span.opener {
    color: #FFF !important;
	background-color: #454545 !important;
}

td {
    background-color: #0000
}

table {
    border: 1px solid #737373;
    border-radius: 4px;
    padding: 5px;
}

.QuizQuestions, .QuizQuestion, .QuestionText, .StdDiv {
    background-color: #404040 !important;
    color: #FFF !important;   
}

.Feedback {
    background-color: #505050 !important;
    color: #FFF !important;   
}

tbody#Questions td {
    color: #FFF !important;   
}

table, div, span, td {
    font-size: 100%;
    color: #FFF;
}
}
