css:
@-moz-document url-prefix("https://sessantaquattro.magicapp.net") {
:root {
  --main_green: #009E60;
  --dark_green: #002C1B;
  --light: #FF9400;
  --lighter: #FFBA5C;
  --back_color: #F1F2EB;
}

body{
    background-color:  var(--back_color);
	font-family: arial, sans-serif !important;
}


td, input{
	font-family: arial, sans-serif !important;
}

.Tabella_Proprieta {
	background-color: rgba(255,255,255,0) !important;
    border: none;
}

#TabellaScheda table{
	border: none;
}

.Tabella_Testata_Titolo_Popup, .Tabella_Testata, .Tabella_Testata_Grande, .grid th {
	background: none;
    background-color: var(--main_green);
    border: 0;
    border-radius: 3px 3px 0 0;
}

#PanelGriglia{
	width:auto !important;
}

#PanelGriglia th{
	border-radius: 0 0;
}

 #TableTestata tbody{
	background-color: rgba(255,255,255,.3);                                    
}


#Table4 tbody td{
	border: 1px solid var(--light);
    display:inline-block;
    height:inherit;
}

#Table4 tbody td input[type=text]{
	height: 25px !important;
}

#TableFunzioni{
	background-color: var(--main_green);
}

.footerstyle{
	background: none;
    background-color: var(--light);
    height: 10px;
}
}

@-moz-document url-prefix("https://sessantaquattro.magicapp.net") {
#ChkSc {
     -webkit-appearance: none;
     -moz-appearance: none;
     appearance: none;
     display: inline-block;
     position: relative;
     background-color: #f1f1f1;
     color: #666;
     top: 3px;
     height:20px;
     width: 20px;
     border: 0;
     cursor: pointer;     
     margin-right: 7px;
     outline: none;
	 border: 1px solid var(--dark_green);
}

#ChkSc:checked::before{
	position: absolute;
     font: 20px/1 'Open Sans', sans-serif;
	 left: 4px;
     top: -2px;
     content: '\02143';
     transform: rotate(40deg);
     color: var(--dark_green);
}

#ChkSc:checked{
	background-color: var(--light);
}

#RndListaTipoLettura_0, #RndListaTipoLettura_1, #RndCausaleMagazzino_0, #RndCausaleMagazzino_1 {
     -webkit-appearance: none;
     -moz-appearance: none;
     appearance: none;
     display: inline-block;
     position: relative;
     background-color: #f1f1f1;
     color: #666;
     top: 3px;
     height: 15px;
     width: 15px;
     border: 0;
     border-radius: 50px;
     cursor: pointer;     
     margin-right: 7px;
     outline: none;
	 border: 1px solid var(--dark_green);
}

#RndListaTipoLettura_0:checked::before, #RndListaTipoLettura_1:checked::before, #RndCausaleMagazzino_0:checked::before, #RndCausaleMagazzino_1:checked::before {
     position: absolute;
     font: 13px/1 'Open Sans', sans-serif;
     left: 3px;
     top: 0px;
     content: '\02143';
     transform: rotate(40deg);
     color: var(--dark_green);
}
#RndListaTipoLettura_0:hover, #RndListaTipoLettura_1:hover, #RndCausaleMagazzino_0:hover, #RndCausaleMagazzino_1:hover {
     background-color: #f7f7f7;
}
#RndListaTipoLettura_0:checked, #RndListaTipoLettura_1:checked, #RndCausaleMagazzino_0:checked, #RndCausaleMagazzino_1:checked 
{
     background-color: var(--light);
}

#TableInternaLettura td{
	padding: 0px;
    height: 1px;
	text-transform: uppercase;
}

#Label13{
	background-color: var(--light);
}

}
