css:
@-moz-document domain("www.audible.ca") {
/* Background of page and even rows of table */
#adbl_page_content,
#center_2 > div.adbl-lib-tab-group > table,
#adbl-footer,
#adbl-footer .reg-footer-wide,
.reg-footer-wide .col,
html,
body,
.bottom-section
{
    background: #333333!important;
    color: white;
}
.bc-box-padding-small {
    padding: 15px;
    background-color: #403f3f;
}

/* Links in header and footer */
#newHeader #nav a,
#adbl-footer > div > div.columns-section > div > a,
#adbl-lib-content table a.adbl-link,
#adbl_pg_library #adbl_page_content a, 
#center_3 > div > div.adbl-lib-content > table tr > td.adbl-flyout-cont-marker.adbl-library-item-title > div.adbl-series-link > div > a
{
    color: white!important;
}

/* Invert logo */
#logo {
    filter: invert(1);
    -webkit-filter: invert(1);
}


/* Title row of table */
#center_3 > div > div.adbl-lib-content > div > table > tbody > tr:nth-child(1) {
    background: black;
}

/* Odd rows of table */
#center_3 > div > div.adbl-lib-content > div > table > tbody > .odd {
    background: #333;
    color: white;
}
.bc-color-base {
    color: #555;
}
.bc-box-padding-base {
    padding: 20px;
    background-color: black;
}
.bc-color-secondary {
    color: rgb(213, 213, 213);
}
.bc-section {
    clear: both;
    overflow: visible;
    background-color: #1e1d1d;
}
.bc-palette-default .bc-popover-inner {
    background-color: #1e1d1d;
    color: white;
}
.bc-table.bc-table-bordered td .bc-table-col-inner, .bc-table.bc-table-bordered th .bc-table-col-inner, .bc-table.bc-table-striped td .bc-table-col-inner, .bc-table.bc-table-striped th .bc-table-col-inner {

    padding: 10px 5px 10px 5px;
    color: white;

}
.bc-color-base {

    color: #d2d2d2;

}
#adbl_page_content, #center_2 > div.adbl-lib-tab-group > table, #adbl-footer, #adbl-footer .reg-footer-wide, .reg-footer-wide .col, html, body, .bottom-section {
    background: #000 !important;
    color: white;
}
.bc-section {
    clear: both;
    overflow: visible;
    background-color: #000;
}
*, ::after, ::before {
    -moz-box-sizing: inherit;
    -webkit-box-sizing: inherit;
    box-sizing: inherit;
    color: white;
    background-color: black;
}
.bc-dropdown .bc-input:hover {
    cursor: pointer;
    background-color: #ffcd4b;
}
.bc-link.bc-color-base:hover {

    color: #ffba06;

}
.bc-link.bc-color-base {

    color: #fff;

}
.bc-dropdown .bc-input {
    cursor: pointer;
    background-color: #1c1c1c;
}
*, ::after, ::before {

    -moz-box-sizing: inherit;
    -webkit-box-sizing: inherit;
    box-sizing: inherit;
    color: white;

}
.bc-autocomplete.bc-autocomplete-has-icon .bc-input {
    padding-right: 36px;
    padding-top: 3px;
    padding-bottom: 3px;
    background-color: black;
}
#center_3 .adbl-lib-content table th
{
    background: #212121!important;
    color: white;
}

/* Library, pre-rders, and wish list buttons */
#adbl_pg_library #adbl_page_content dbl-lib-tab-group table tr td a,
#adbl_pg_library #adbl_page_content #center_2 > div.adbl-lib-tab-group > table > tbody > tr > td > a
{
    color: orange!important;
}
}
