css:
@-moz-document regexp(".*\\.google\\.com/(?!maps).*") {
/* Conwy - Google
   Version:  1.2.15
   Author:   lineav
   Based on: https://github.com/pyxelr/Dark_Google_Calendar
   Licence:  https://creativecommons.org/licenses/by-sa/4.0/
*/
/* Invert the whole website */
html {
    filter: invert(0.80) hue-rotate(180deg) contrast(1.2) !important;
    background: #ffffff !important;
}
/* Hangouts bar */
body {
    background: #ffffff !important;
}
iframe {
    filter: invert(1) hue-rotate(180deg) contrast(1.2)!important;
}
/* Inversion of images, svg graphics (avatars, icons) */
.gbip:before, img, svg:not(.RTiFqe):not(#icon):not(.iron-icon), .RY3tic, .gb_Aa, .gb_Ea, .gb_Ia, .gb_s {
    filter: invert(1) hue-rotate(180deg) brightness(1.1) contrast(1.05)!important;
}
/* invert G Suite logo */
img.gb_ua {
    filter: invert(0) !important;
}
/* Inversion of video and canvas */
video {
    filter: invert(1) hue-rotate(180deg)!important;
}
/* Inversion of guest avatars attending the event */
.jPtXgd {
    filter: invert(1) hue-rotate(180deg) brightness(1.1) contrast(1.05)!important;
}
/* Inversion of avatar in apps menu */
.gb_q {
    filter: invert(1) hue-rotate(180deg) brightness(1.1) contrast(1.05)!important;
}
/* Inversion of avatar in gmail address suggestions */
.azp {
    filter: invert(1) hue-rotate(180deg) brightness(1.1) contrast(1.05)!important;
}
/* gmail reply and forward images and other button icons */
.ams::before {
    filter: invert(0) hue-rotate(180deg) !important;    
}
.hk>.L3, .acX>.L3, .iG {
    filter: invert(1) hue-rotate(180deg) !important;    
}

/* darken background on popup in contacts, gmail compose, calendar */
.llhEMd.llhEMd, .aSs, .NBxL9e, div.Kj-JD-Jh {
    background-color: rgba(255, 255, 255, 0.5)
}
/* darker header in gmail compose */
.Hy .m {
    background: #888;
}
/* make borders on meet video screen dark */
.p2ZbV, .zWfAib.PvRhvb>.Zf0RDc[data-allocation-index="0"] {
    background: #fff;
}
/* meet speaker volume indicator*/
.pZFrDd {
    filter: invert(1) hue-rotate(180deg);
}

}
