css:
@-moz-document domain("theguardian.com") {
/* Conwy - The Guardian
   Version:  1.1.1
   Author:   lineav
   Licence:  https://creativecommons.org/licenses/by-sa/4.0/
*/

/* Invert the whole website */
html {
    filter: invert(0.80) hue-rotate(180deg) contrast(1.2)!important;
    background: #fff;
}

img, .youtube-media-atom__overlay, svg.inline-the-guardian-logo__svg, .top-bar__item, figure.element-video {
    filter: invert(1) hue-rotate(180deg) brightness(1.1) contrast(1.05)!important;
}

.new-header, .menu, .menu__inner, .menu-group--primary, .menu-group--secondary, .l-footer, .footer__back-to-top {
    background-color: #b6d1fb;
}

.pillar-link, .menu-item__title, .colophon__item a {
    color: #000;
}

/** invert twitter posts back to have images shown with correct colours */
twitter-widget {
    filter: invert(1) hue-rotate(180deg) brightness(1.1) contrast(1.05)!important;
}

/* make scrollbars dark */
::-webkit-scrollbar {
    background: #2f2f2f;
    width: 0.8em;
}
::-webkit-scrollbar-thumb {
  background-color: #555555;
  outline: 1px solid #111111;
}
}
