/* Invert the whole website */
html {
    filter: invert(0.80) hue-rotate(180deg) contrast(1.2)!important;
    background: #ffffff!important;
}

body {
	background-color: #ffffff !important;
}

.list {
	background-color: #f0f0f0;
}

.list-card {
	background-color: #dddddd;
}

.list-header-name {
 	color: #666666;
}

.list-card:hover,
.open-card-composer:hover {
  background-color: #cccccc;
}

.js-add-another-card, .js-add-a-card {
    color: #aaaaaa;
}

.window-overlay, .quick-card-editor {
    background-color: rgba(255, 255, 255, 0.64)
}

div#board {
    background-color: #ffffff;    
}

.board-header {
    background-color: #dddddd;
}

.board-header-btn-text {
    color: #999999;
}

/* header elements */

.body-board-view #header {
    background: rgba(255,255, 255, 0.85)
}

