css:
@-moz-document domain("kissmanga.com") {
iframe[title="Disqus"],
.specialButton {
    display: none;
}

input.text {
    background: #393939;
}

.scrollable {
    height: 200px
}
#selectReadType {
    width: 90px !important;
}
.selectChapter {
    width: 330px !important;
}

div img[onerror] {
    max-width: 95vw;
}
.boxDropDown {
    font-size: 1rem;
    width: 150px !important;
}
#menu_box {
    font-size: 1rem;
}

.btnNext,
.btnPrevious {
    height: 35px;
    position: relative;
    top: -6px;
    display:inline-block;
}

.btnNext:hover,
.btnPrevious:hover {
    filter: brightness(1.5);
}

#container {
    width: 95vw;
}
#leftside {
    width: auto;
}
#rightside {
    display: none;
}
#footer {
    display: none;
}
bigBarContainer {
    display: none;
}
#navsubbar,
.barTitle,
#navcontainer li a#currentTab {
    background: #0481BC none;
}
#navcontainer li a {
    border-radius: 5px 5px 0 0;
}

#navcontainer a:link,
#navcontainer a:visited {
    background: #ccc none;
    box-shadow: inset 1px -2px 5px rgba(0, 0, 0, .3);
}

.info {
    color: #ddd;
}
html,
body {
    font: 100% tahoma, Arial, Helvetica, sans-serif;
    line-height: 1.5;
    ;
}
.listing tr:nth-child(odd) td {
    background-color: #323232;
    color: white;
    padding: 3px;
}
.listing a {
    color: #91d9ff;
    text-decoration: underline;
}
a {
    text-decoration: underline;
}
@media (min-width: 1000px) {

    #rightside {
        padding: 0;
        width: 220px;
        text-align: left;
        float: right;
        display: block !important;
    }
    /*    #leftside{
width:calc(100% - 120px);
}
*/
    #container {
        width: 1000px;
    }
    #leftside {
        width: 730px;
    }
}
@media (min-width: 1200px) {

    #container {
        width: 1200px;
    }
    #leftside {
        width: 930px;
    }
}
}
