css:
@-moz-document regexp("^https?://localhost:8001(.)*$") {
.kd-app-entire-content, body {
    background-color: #232831;
}

.kd-app-content {
    background-color: #232831;
}

.kd-content-card-content {
    background-color: #1a2029;
}

md-select-menu {
    border-radius: 5px;
}

md-content.md-default-theme, md-content {
    color: #eeeeeec7;
}

.kd-chips {
    background-color: #eeeeee0f;
}

.kd-resource-card-header-columns {
    color: #636363;
    font-weight: 600;
}

md-toolbar:not(.md-menu-toolbar) {
    background-color: #5a3196;
}

md-select.kd-namespace-select .md-select-value span {
    color: #7f41b7;
}

md-select-menu.md-default-theme md-content, md-select-menu md-content {
    background: #282d36;
}

md-select-menu.md-default-theme md-content md-option, md-select-menu md-content md-option {
    color: #eeeeee9c;
}

kd-namespace-select .kd-namespace-select-title {
    color: #ffffff38;
}

a {
    color: #326de6;
}

md-icon.md-default-theme, md-icon {
    color: #585858;
}

kd-chrome .kd-toolbar {
    background-color: #00000012;
}

md-input-container.md-default-theme .md-input, md-input-container .md-input {
    color: rgb(255, 255, 255);
}

md-select-menu.md-default-theme md-content md-option:not([disabled]):focus, md-select-menu md-content md-option:not([disabled]):focus, md-select-menu.md-default-theme md-content md-option:not([disabled]):hover, md-select-menu md-content md-option:not([disabled]):hover {
    background: rgba(238, 238, 238, 0.05);
}

.kd-success {
    color: #32b33294;
}

.kd-error {
    color: #ce4646;
}

icon.md-warn {
    color: #f3c237c9;
}

icon.md-warn {
    color: #ff5722d6;
}

.kd-nav-item-button.kd-nav-item-active {
    color: #a96717;
    font-weight: bold;
}

.kd-error-block {
    background-color: #8a8a8a4d;
}

.kd-logs-text-color-invert {
    background-color: #171717;
    color: #c7c7c7;
}

.kd-logs-element {
    font-size: 13px;
}

md-select-menu.md-default-theme md-content md-option[selected], md-select-menu md-content md-option[selected] {
    color: #e68d32;
}

md-select-menu.md-default-theme md-content md-option[selected]:focus, md-select-menu md-content md-option[selected]:focus {
    color: #efb579;
}

md-select.kd-namespace-select .md-select-value {
    background-color: #00000029;
    padding: 6px 13px;
}

input[type=search] {
    background-color: #0000006e;
    padding: 10px 10px;
    color: gray;
    border-bottom: 2px solid #3c3c3c;
}

.nvd3.nv-pie path {
    /* stroke: #afafafa8; */
    stroke: #989898a8;
}

.kd-resource-card-header-column-sortable md-icon:not(.kd-resource-card-header-column-sorted) {
    color: rgba(134, 134, 134, 0.38);
}

.kd-resource-card-header-column-sorted {
    color: rgba(255, 255, 255, 0.61);
}

.kd-resource-card-header-column-sortable:hover:not(.kd-resource-card-header-column-sorted) * {
    color: rgba(62, 62, 62, 0.87);
}

md-menu-content.md-default-theme, md-menu-content {
    background-color: rgb(27, 27, 27);
}

md-menu-content.md-default-theme md-menu-item, md-menu-content md-menu-item {
    color: rgba(165, 165, 165, 0.87);
}

.kd-logs-icon {
    color: #6d6d6d;
}

.kd-logs-color-icon-invert {
    background-color: #444;
}

md-dialog.md-default-theme, md-dialog {
    background-color: #131313;
    color: rgba(189, 189, 189, 0.87);
}
}
